// UIL Science Trainer – Starter Question Bank
// Format: { id, subject, topic, question, choices: [A,B,C,D], answer (0-3), explanation }

const SCIENCE_QUESTIONS = [

  // ═══════════════════════════════════════════
  //  BIOLOGY
  // ═══════════════════════════════════════════

  // Cell Biology
  { id: "b1", subject: "biology", topic: "Cell Biology", question: "Which organelle is responsible for producing ATP through cellular respiration?", choices: ["Ribosome", "Mitochondria", "Golgi apparatus", "Endoplasmic reticulum"], answer: 1, explanation: "Mitochondria are the 'powerhouses' of the cell, converting glucose and oxygen into ATP through oxidative phosphorylation." },
  { id: "b2", subject: "biology", topic: "Cell Biology", question: "What is the primary function of the rough endoplasmic reticulum?", choices: ["Lipid synthesis", "Protein synthesis and transport", "Cell division", "Energy production"], answer: 1, explanation: "The rough ER is studded with ribosomes and is responsible for synthesizing and transporting proteins." },
  { id: "b3", subject: "biology", topic: "Cell Biology", question: "During which phase of mitosis do chromosomes line up along the cell's equator?", choices: ["Prophase", "Metaphase", "Anaphase", "Telophase"], answer: 1, explanation: "During metaphase, chromosomes align along the metaphase plate (cell equator) before being separated." },
  { id: "b4", subject: "biology", topic: "Cell Biology", question: "Which type of transport requires energy to move molecules against their concentration gradient?", choices: ["Osmosis", "Facilitated diffusion", "Active transport", "Simple diffusion"], answer: 2, explanation: "Active transport uses ATP to move molecules from low to high concentration, against the gradient." },

  // Genetics
  { id: "b5", subject: "biology", topic: "Genetics", question: "If a heterozygous tall plant (Tt) is crossed with a homozygous short plant (tt), what percentage of offspring are expected to be short?", choices: ["0%", "25%", "50%", "75%"], answer: 2, explanation: "Tt × tt produces Tt and tt in a 1:1 ratio, so 50% of offspring will be short (tt)." },
  { id: "b6", subject: "biology", topic: "Genetics", question: "Which type of RNA carries amino acids to the ribosome during translation?", choices: ["mRNA", "tRNA", "rRNA", "snRNA"], answer: 1, explanation: "Transfer RNA (tRNA) carries specific amino acids to the ribosome, matching its anticodon to the mRNA codon." },
  { id: "b7", subject: "biology", topic: "Genetics", question: "A point mutation that changes a codon to a stop codon is called a:", choices: ["Missense mutation", "Silent mutation", "Nonsense mutation", "Frameshift mutation"], answer: 2, explanation: "A nonsense mutation creates a premature stop codon, resulting in a truncated (shortened) protein." },
  { id: "b8", subject: "biology", topic: "Genetics", question: "In DNA replication, which enzyme unwinds the double helix?", choices: ["DNA polymerase", "Helicase", "Ligase", "Primase"], answer: 1, explanation: "Helicase breaks the hydrogen bonds between base pairs, unwinding and separating the two DNA strands." },

  // Ecology
  { id: "b9", subject: "biology", topic: "Ecology", question: "Which trophic level has the most energy available in an ecosystem?", choices: ["Primary consumers", "Secondary consumers", "Tertiary consumers", "Producers"], answer: 3, explanation: "Producers (autotrophs) have the most energy. Only about 10% of energy transfers to each successive trophic level." },
  { id: "b10", subject: "biology", topic: "Ecology", question: "What type of relationship exists when one organism benefits while the other is neither helped nor harmed?", choices: ["Mutualism", "Parasitism", "Commensalism", "Competition"], answer: 2, explanation: "Commensalism is a symbiotic relationship where one organism benefits and the other is unaffected." },
  { id: "b11", subject: "biology", topic: "Ecology", question: "The process by which nitrogen gas (N₂) is converted to ammonia (NH₃) by bacteria is called:", choices: ["Denitrification", "Nitrogen fixation", "Ammonification", "Nitrification"], answer: 1, explanation: "Nitrogen fixation converts atmospheric N₂ into ammonia, primarily by Rhizobium bacteria in root nodules of legumes." },

  // Evolution
  { id: "b12", subject: "biology", topic: "Evolution", question: "Structures that are similar in different species due to common ancestry are called:", choices: ["Analogous structures", "Homologous structures", "Vestigial structures", "Convergent structures"], answer: 1, explanation: "Homologous structures share a common evolutionary origin but may have different functions (e.g., human arm, whale flipper, bat wing)." },
  { id: "b13", subject: "biology", topic: "Evolution", question: "Which mechanism of evolution involves random changes in allele frequencies, especially in small populations?", choices: ["Natural selection", "Gene flow", "Genetic drift", "Mutation"], answer: 2, explanation: "Genetic drift is the random fluctuation of allele frequencies, having the greatest effect in small populations (bottleneck and founder effects)." },

  // Body Systems
  { id: "b14", subject: "biology", topic: "Body Systems", question: "Which part of the nephron is primarily responsible for water reabsorption?", choices: ["Bowman's capsule", "Loop of Henle", "Distal tubule", "Collecting duct"], answer: 1, explanation: "The Loop of Henle creates a concentration gradient in the kidney medulla, enabling water reabsorption from the filtrate." },
  { id: "b15", subject: "biology", topic: "Body Systems", question: "The sinoatrial (SA) node is often called the 'pacemaker' because it:", choices: ["Pumps blood to the lungs", "Initiates the heartbeat electrical signal", "Separates oxygenated and deoxygenated blood", "Controls blood pressure"], answer: 1, explanation: "The SA node generates electrical impulses that set the rhythm for the heart's contractions." },
  { id: "b16", subject: "biology", topic: "Body Systems", question: "Which type of white blood cell produces antibodies?", choices: ["Neutrophils", "B lymphocytes (plasma cells)", "Macrophages", "Eosinophils"], answer: 1, explanation: "B lymphocytes differentiate into plasma cells that produce antibodies specific to antigens." },

  // Taxonomy
  { id: "b17", subject: "biology", topic: "Taxonomy", question: "Which domain includes all multicellular organisms with membrane-bound organelles?", choices: ["Bacteria", "Archaea", "Eukarya", "Protista"], answer: 2, explanation: "Eukarya includes all organisms with eukaryotic cells — protists, fungi, plants, and animals." },
  { id: "b18", subject: "biology", topic: "Taxonomy", question: "In binomial nomenclature, which taxonomic levels make up an organism's scientific name?", choices: ["Kingdom and Phylum", "Family and Order", "Genus and Species", "Class and Order"], answer: 2, explanation: "Binomial nomenclature uses the genus and species names (e.g., Homo sapiens)." },

  // Photosynthesis & Respiration
  { id: "b19", subject: "biology", topic: "Photosynthesis", question: "In which stage of photosynthesis is oxygen produced?", choices: ["Calvin cycle", "Light-dependent reactions", "Glycolysis", "Krebs cycle"], answer: 1, explanation: "Oxygen is a byproduct of the light-dependent reactions when water molecules are split (photolysis) in Photosystem II." },
  { id: "b20", subject: "biology", topic: "Photosynthesis", question: "The Calvin cycle occurs in which part of the chloroplast?", choices: ["Thylakoid membrane", "Stroma", "Outer membrane", "Intermembrane space"], answer: 1, explanation: "The Calvin cycle (light-independent reactions) occurs in the stroma, using CO₂ and ATP/NADPH from the light reactions to produce G3P." },

  // Cell Biology (continued)
  { id: "b21", subject: "biology", topic: "Cell Biology", question: "Which checkpoint in the cell cycle ensures all chromosomes are properly attached to spindle fibers before proceeding to anaphase?", choices: ["G1 checkpoint", "G2 checkpoint", "M checkpoint (spindle assembly checkpoint)", "S checkpoint"], answer: 2, explanation: "The M checkpoint (spindle assembly checkpoint) monitors kinetochore-spindle attachments and prevents anaphase until all chromosomes are properly connected to the mitotic spindle." },
  { id: "b22", subject: "biology", topic: "Cell Biology", question: "Which organelle contains hydrolytic enzymes and functions as the cell's digestive system?", choices: ["Peroxisome", "Lysosome", "Smooth ER", "Vacuole"], answer: 1, explanation: "Lysosomes contain acid hydrolases that break down macromolecules, old organelles, and engulfed pathogens. They maintain an internal pH of about 5." },
  { id: "b23", subject: "biology", topic: "Cell Biology", question: "In the fluid mosaic model of the cell membrane, cholesterol functions primarily to:", choices: ["Transport glucose across the membrane", "Moderate membrane fluidity across temperature changes", "Anchor the cytoskeleton to the membrane", "Facilitate signal transduction"], answer: 1, explanation: "Cholesterol inserts between phospholipids, reducing fluidity at high temperatures and preventing solidification at low temperatures, thereby buffering membrane fluidity." },
  { id: "b24", subject: "biology", topic: "Cell Biology", question: "During which phase of the cell cycle does DNA replication occur?", choices: ["G1 phase", "S phase", "G2 phase", "M phase"], answer: 1, explanation: "The S (synthesis) phase is when all chromosomal DNA is replicated, resulting in sister chromatids joined at centromeres." },
  { id: "b25", subject: "biology", topic: "Cell Biology", question: "A G protein-coupled receptor activates a signal transduction pathway by:", choices: ["Directly phosphorylating target proteins", "Activating a G protein that then activates an effector enzyme", "Opening an ion channel in the membrane", "Transporting the ligand into the nucleus"], answer: 1, explanation: "When a ligand binds a GPCR, it causes the associated G protein to exchange GDP for GTP, activating it. The active G protein then stimulates effector enzymes like adenylyl cyclase." },
  { id: "b26", subject: "biology", topic: "Cell Biology", question: "Which feature is found in prokaryotic cells but NOT in eukaryotic cells?", choices: ["Ribosomes", "Cell membrane", "A single circular chromosome in the nucleoid region", "Cytoplasm"], answer: 2, explanation: "Prokaryotes have a single circular chromosome located in a nucleoid region without a membrane-bound nucleus. Eukaryotes have linear chromosomes enclosed in a nuclear envelope." },

  // Genetics (continued)
  { id: "b27", subject: "biology", topic: "Genetics", question: "In a dihybrid cross between two heterozygous individuals (AaBb x AaBb), what fraction of offspring are expected to display both dominant phenotypes?", choices: ["1/16", "3/16", "9/16", "12/16"], answer: 2, explanation: "A dihybrid cross of two heterozygotes yields a 9:3:3:1 phenotypic ratio. 9/16 of offspring display both dominant traits." },
  { id: "b28", subject: "biology", topic: "Genetics", question: "During DNA replication, Okazaki fragments are formed on the:", choices: ["Leading strand only", "Lagging strand only", "Both strands equally", "Neither strand — they form during transcription"], answer: 1, explanation: "Because DNA polymerase synthesizes only in the 5' to 3' direction, the lagging strand is synthesized discontinuously as short Okazaki fragments that are later joined by DNA ligase." },
  { id: "b29", subject: "biology", topic: "Genetics", question: "During transcription, RNA polymerase reads the template strand in which direction?", choices: ["5' to 3'", "3' to 5'", "Both directions simultaneously", "Direction depends on the gene"], answer: 1, explanation: "RNA polymerase reads the DNA template strand 3' to 5', synthesizing the mRNA strand in the 5' to 3' direction." },
  { id: "b30", subject: "biology", topic: "Genetics", question: "The wobble hypothesis explains why:", choices: ["Some codons code for the same amino acid", "The third base of a codon can form non-standard base pairs with the anticodon", "Mutations always change the amino acid sequence", "tRNA molecules can carry multiple amino acids"], answer: 1, explanation: "The wobble hypothesis states that the third position of a codon can form flexible (wobble) base pairs with the first position of the anticodon, allowing one tRNA to recognize multiple codons for the same amino acid." },
  { id: "b31", subject: "biology", topic: "Genetics", question: "A lac operon in E. coli is induced when:", choices: ["Glucose is abundant and lactose is absent", "Lactose is present and glucose is absent", "Both glucose and lactose are absent", "Both glucose and lactose are present"], answer: 1, explanation: "The lac operon is maximally expressed when lactose is present (allolactose inactivates the repressor) and glucose is absent (cAMP levels rise, activating CAP to enhance transcription)." },
  { id: "b32", subject: "biology", topic: "Genetics", question: "In gel electrophoresis, DNA fragments migrate toward the positive electrode because DNA is:", choices: ["Positively charged due to histone proteins", "Negatively charged due to phosphate groups", "Neutral but carried by buffer flow", "Positively charged due to nitrogenous bases"], answer: 1, explanation: "DNA has a net negative charge from its phosphate backbone. In an electric field, DNA fragments migrate toward the anode (positive electrode), with smaller fragments moving faster through the gel." },

  // Ecology (continued)
  { id: "b33", subject: "biology", topic: "Ecology", question: "Which biome is characterized by permafrost, low temperatures, and a short growing season?", choices: ["Taiga", "Tundra", "Temperate grassland", "Desert"], answer: 1, explanation: "The tundra biome is defined by permafrost (permanently frozen subsoil), extremely cold temperatures, and a very short growing season that supports low-growing vegetation like mosses and lichens." },
  { id: "b34", subject: "biology", topic: "Ecology", question: "A population growing in an environment with limited resources will typically show:", choices: ["Exponential growth indefinitely", "Logistic growth approaching a carrying capacity", "Linear growth at a constant rate", "Declining growth from the start"], answer: 1, explanation: "When resources are limited, populations follow logistic growth, initially growing rapidly then slowing as they approach the carrying capacity (K) of the environment." },
  { id: "b35", subject: "biology", topic: "Ecology", question: "In the phosphorus cycle, unlike the carbon and nitrogen cycles, phosphorus does NOT:", choices: ["Cycle through living organisms", "Exist in soil and rock", "Enter the atmosphere as a gas under normal conditions", "Dissolve in water"], answer: 2, explanation: "Phosphorus lacks a significant gaseous phase. It cycles through rocks, soil, water, and organisms, but does not normally enter the atmosphere as a gas, unlike carbon (CO2) and nitrogen (N2)." },
  { id: "b36", subject: "biology", topic: "Ecology", question: "During primary succession, the first organisms to colonize bare rock are typically:", choices: ["Grasses and ferns", "Lichens and mosses", "Shrubs and small trees", "Annual wildflowers"], answer: 1, explanation: "Lichens and mosses are pioneer species that colonize bare rock. They secrete acids that break down rock into soil, gradually allowing other plants to establish." },
  { id: "b37", subject: "biology", topic: "Ecology", question: "Biological magnification refers to the process by which:", choices: ["Organisms grow larger at higher trophic levels", "Toxin concentrations increase at each successive trophic level", "Species diversity increases in warmer climates", "Populations grow exponentially without predation"], answer: 1, explanation: "Biological magnification (biomagnification) is the accumulation of persistent toxins (like DDT or mercury) in increasing concentrations at higher trophic levels of a food chain." },

  // Evolution (continued)
  { id: "b38", subject: "biology", topic: "Evolution", question: "Stabilizing selection tends to:", choices: ["Favor individuals at one extreme of a trait", "Favor individuals at both extremes of a trait", "Favor individuals with intermediate phenotypes", "Eliminate all variation in a population"], answer: 2, explanation: "Stabilizing selection reduces variation by selecting against extreme phenotypes and favoring intermediate ones. Birth weight in humans is a classic example." },
  { id: "b39", subject: "biology", topic: "Evolution", question: "Two populations of squirrels are separated by a canyon and over time become different species. This is an example of:", choices: ["Sympatric speciation", "Allopatric speciation", "Parapatric speciation", "Artificial selection"], answer: 1, explanation: "Allopatric speciation occurs when a physical barrier geographically isolates populations, allowing them to diverge genetically over time." },
  { id: "b40", subject: "biology", topic: "Evolution", question: "The Hardy-Weinberg equation p² + 2pq + q² = 1 represents:", choices: ["The rate of mutation in a population", "The expected genotype frequencies in a non-evolving population", "The ratio of dominant to recessive alleles", "The rate of natural selection"], answer: 1, explanation: "The Hardy-Weinberg equation predicts genotype frequencies (p² = homozygous dominant, 2pq = heterozygous, q² = homozygous recessive) in a population that is not evolving." },
  { id: "b41", subject: "biology", topic: "Evolution", question: "Similar bone structures in the forelimbs of humans, cats, whales, and bats are examples of:", choices: ["Convergent evolution", "Analogous structures", "Homologous structures", "Vestigial structures"], answer: 2, explanation: "These forelimb bones share a common structural plan inherited from a common ancestor but have been modified for different functions, making them homologous structures." },
  { id: "b42", subject: "biology", topic: "Evolution", question: "On a phylogenetic tree, a node (branch point) represents:", choices: ["An extinction event", "A common ancestor from which lineages diverged", "A mutation event", "A point of convergent evolution"], answer: 1, explanation: "Each node on a phylogenetic tree represents a most recent common ancestor from which two or more descendant lineages diverged." },

  // Body Systems (continued)
  { id: "b43", subject: "biology", topic: "Body Systems", question: "The synapse between two neurons transmits a signal using:", choices: ["Direct electrical contact in all cases", "Neurotransmitters released from synaptic vesicles", "Hormones carried in the blood", "Ions that pass directly through gap junctions only"], answer: 1, explanation: "At chemical synapses, action potentials trigger the release of neurotransmitters from synaptic vesicles into the synaptic cleft, where they bind to receptors on the postsynaptic neuron." },
  { id: "b44", subject: "biology", topic: "Body Systems", question: "The hypothalamus regulates the anterior pituitary gland by secreting:", choices: ["Antibodies into the bloodstream", "Releasing and inhibiting hormones into a portal system", "Digestive enzymes into the duodenum", "Neurotransmitters directly into target organs"], answer: 1, explanation: "The hypothalamus produces releasing hormones (e.g., GnRH, TRH) and inhibiting hormones that travel through the hypophyseal portal system to stimulate or suppress anterior pituitary hormone secretion." },
  { id: "b45", subject: "biology", topic: "Body Systems", question: "Most chemical digestion and nutrient absorption occurs in the:", choices: ["Stomach", "Small intestine", "Large intestine", "Esophagus"], answer: 1, explanation: "The small intestine is the primary site of chemical digestion (aided by pancreatic enzymes and bile) and nutrient absorption through its villi and microvilli." },
  { id: "b46", subject: "biology", topic: "Body Systems", question: "Gas exchange in the lungs occurs at the:", choices: ["Bronchi", "Bronchioles", "Alveoli", "Trachea"], answer: 2, explanation: "Alveoli are tiny air sacs with thin walls surrounded by capillaries, providing a large surface area for efficient diffusion of O₂ into the blood and CO₂ out of the blood." },
  { id: "b47", subject: "biology", topic: "Body Systems", question: "The functional unit of a skeletal muscle, bounded by Z lines, is called a:", choices: ["Myofibril", "Sarcomere", "Fascicle", "Motor unit"], answer: 1, explanation: "A sarcomere is the basic contractile unit of skeletal muscle, extending from one Z line to the next, containing overlapping actin and myosin filaments." },
  { id: "b48", subject: "biology", topic: "Body Systems", question: "In human reproduction, fertilization most commonly occurs in the:", choices: ["Uterus", "Fallopian tube (oviduct)", "Ovary", "Cervix"], answer: 1, explanation: "Fertilization typically occurs in the ampulla of the fallopian tube. The resulting zygote then travels to the uterus over several days, where it implants in the endometrium." },

  // Taxonomy (continued)
  { id: "b49", subject: "biology", topic: "Taxonomy", question: "Which kingdom contains organisms that are multicellular, heterotrophic, and digest food externally before absorbing it?", choices: ["Animalia", "Plantae", "Fungi", "Protista"], answer: 2, explanation: "Fungi are multicellular (with the exception of yeasts), heterotrophic organisms that secrete digestive enzymes externally and absorb the resulting small molecules." },
  { id: "b50", subject: "biology", topic: "Taxonomy", question: "The correct hierarchical order of taxonomic classification from broadest to most specific is:", choices: ["Kingdom, Phylum, Class, Order, Family, Genus, Species", "Kingdom, Class, Phylum, Order, Family, Genus, Species", "Domain, Phylum, Kingdom, Class, Order, Family, Genus, Species", "Kingdom, Phylum, Order, Class, Family, Genus, Species"], answer: 0, explanation: "The standard taxonomic hierarchy is: Domain, Kingdom, Phylum, Class, Order, Family, Genus, Species. The mnemonic 'King Philip Came Over For Good Spaghetti' helps remember from Kingdom onward." },
  { id: "b51", subject: "biology", topic: "Taxonomy", question: "In a cladogram, a shared derived character (synapomorphy) is used to:", choices: ["Identify species that live in the same habitat", "Group organisms into a clade based on common ancestry", "Determine the age of a fossil", "Measure genetic drift in a population"], answer: 1, explanation: "Synapomorphies are shared derived characters inherited from a common ancestor that define a clade." },
  { id: "b52", subject: "biology", topic: "Taxonomy", question: "Which of the following is an example of a paraphyletic group?", choices: ["Mammals", "Reptiles (excluding birds)", "Birds", "Insects"], answer: 1, explanation: "Traditional Reptilia is paraphyletic because it excludes birds, which evolved from within the reptile lineage. A monophyletic group includes the common ancestor and ALL of its descendants." },

  // Photosynthesis (continued)
  { id: "b53", subject: "biology", topic: "Photosynthesis", question: "In the light-dependent reactions, water molecules are split at:", choices: ["Photosystem I", "Photosystem II", "The cytochrome b6f complex", "ATP synthase"], answer: 1, explanation: "Photolysis of water occurs at Photosystem II. Water is split into 2H⁺, ½O₂, and electrons that replace those excited out of the P680 reaction center chlorophyll." },
  { id: "b54", subject: "biology", topic: "Photosynthesis", question: "C4 plants like corn avoid photorespiration by:", choices: ["Closing stomata permanently during the day", "Fixing CO₂ initially into a 4-carbon compound in mesophyll cells, then releasing it to bundle-sheath cells", "Performing the Calvin cycle only at night", "Using a different enzyme instead of RuBisCO"], answer: 1, explanation: "C4 plants use PEP carboxylase in mesophyll cells to fix CO₂ into oxaloacetate (4C), which is shuttled to bundle-sheath cells where CO₂ is released at high concentration for RuBisCO and the Calvin cycle." },
  { id: "b55", subject: "biology", topic: "Photosynthesis", question: "In the chloroplast electron transport chain, the final electron acceptor is:", choices: ["O₂", "NADP⁺", "H₂O", "CO₂"], answer: 1, explanation: "In the light reactions, electrons from Photosystem I are transferred to NADP⁺ along with H⁺ ions, reducing it to NADPH, which is used in the Calvin cycle to fix carbon." },
  { id: "b56", subject: "biology", topic: "Photosynthesis", question: "Thylakoid membranes are arranged in stacks called:", choices: ["Cristae", "Grana", "Stroma lamellae", "Cisternae"], answer: 1, explanation: "Grana (singular: granum) are stacks of thylakoid membranes within the chloroplast. They contain the photosystems and ATP synthase for the light reactions." },

  // Cellular Respiration
  { id: "b57", subject: "biology", topic: "Cellular Respiration", question: "Glycolysis takes place in the _____ and produces a net gain of _____ ATP per glucose molecule.", choices: ["Cytoplasm; 2", "Mitochondrial matrix; 2", "Cytoplasm; 4", "Mitochondrial matrix; 36"], answer: 0, explanation: "Glycolysis occurs in the cytoplasm and produces a gross total of 4 ATP but uses 2 ATP in the energy investment phase, yielding a net gain of 2 ATP per glucose." },
  { id: "b58", subject: "biology", topic: "Cellular Respiration", question: "In the Krebs cycle, acetyl-CoA combines with oxaloacetate to form:", choices: ["Pyruvate", "Citrate", "Succinate", "Malate"], answer: 1, explanation: "The first step of the Krebs (citric acid) cycle is the condensation of the 2-carbon acetyl group from acetyl-CoA with the 4-carbon oxaloacetate to form the 6-carbon molecule citrate." },
  { id: "b59", subject: "biology", topic: "Cellular Respiration", question: "The electron transport chain is located on the:", choices: ["Outer mitochondrial membrane", "Inner mitochondrial membrane", "Mitochondrial matrix", "Cytoplasmic membrane"], answer: 1, explanation: "The electron transport chain (ETC) is embedded in the inner mitochondrial membrane (cristae). Electrons from NADH and FADH₂ pass through protein complexes, pumping H⁺ ions into the intermembrane space." },
  { id: "b60", subject: "biology", topic: "Cellular Respiration", question: "What is the maximum theoretical yield of ATP from the complete oxidation of one glucose molecule through aerobic respiration?", choices: ["2 ATP", "4 ATP", "30-32 ATP", "38 ATP"], answer: 2, explanation: "Current estimates place the maximum yield at 30-32 ATP per glucose: 2 from glycolysis, 2 from the Krebs cycle, and 26-28 from oxidative phosphorylation." },
  { id: "b61", subject: "biology", topic: "Cellular Respiration", question: "In alcoholic fermentation by yeast, pyruvate is converted to:", choices: ["Lactate and NAD⁺", "Ethanol and CO₂", "Acetyl-CoA and CO₂", "Citrate and NADH"], answer: 1, explanation: "In alcoholic fermentation, pyruvate is first decarboxylated to acetaldehyde (releasing CO₂), then reduced to ethanol by NADH, regenerating NAD⁺ so glycolysis can continue." },

  // Molecular Biology
  { id: "b62", subject: "biology", topic: "Molecular Biology", question: "Chargaff's rules state that in DNA:", choices: ["Adenine pairs with guanine, and cytosine pairs with thymine", "The amount of adenine equals thymine, and guanine equals cytosine", "All four bases are present in equal amounts", "Purines are always more abundant than pyrimidines"], answer: 1, explanation: "Chargaff's rules state that A = T and G = C in double-stranded DNA, reflecting Watson-Crick base pairing." },
  { id: "b63", subject: "biology", topic: "Molecular Biology", question: "The primary structure of a protein refers to:", choices: ["Its three-dimensional folded shape", "The linear sequence of amino acids in the polypeptide chain", "Alpha helices and beta pleated sheets", "The assembly of multiple polypeptide subunits"], answer: 1, explanation: "Primary structure is the unique linear sequence of amino acids linked by peptide bonds. This sequence is determined by the gene and dictates all higher levels of protein structure." },
  { id: "b64", subject: "biology", topic: "Molecular Biology", question: "Enzymes increase the rate of chemical reactions by:", choices: ["Increasing the temperature of the reaction", "Lowering the activation energy of the reaction", "Changing the equilibrium of the reaction", "Increasing the free energy change of the reaction"], answer: 1, explanation: "Enzymes are biological catalysts that lower the activation energy (Ea) by stabilizing the transition state. They do not change the equilibrium or the overall free energy change." },
  { id: "b65", subject: "biology", topic: "Molecular Biology", question: "Alternative splicing of mRNA allows:", choices: ["One gene to code for multiple different proteins", "DNA to replicate more quickly", "Ribosomes to translate faster", "Mutations to be repaired before translation"], answer: 0, explanation: "Alternative splicing removes different combinations of introns and exons from pre-mRNA, enabling a single gene to produce multiple distinct mRNA variants and thus different protein isoforms." },
  { id: "b66", subject: "biology", topic: "Molecular Biology", question: "DNA methylation is an epigenetic modification that typically:", choices: ["Activates gene expression by loosening chromatin", "Silences gene expression without changing the DNA sequence", "Repairs damaged nucleotides during replication", "Increases the mutation rate of affected genes"], answer: 1, explanation: "DNA methylation (addition of methyl groups to cytosine bases) typically represses transcription by recruiting proteins that condense chromatin, silencing genes without altering the DNA sequence." },

  // Biochemistry
  { id: "b67", subject: "biology", topic: "Biochemistry", question: "Which macromolecule is composed of monosaccharide monomers linked by glycosidic bonds?", choices: ["Protein", "Nucleic acid", "Carbohydrate", "Lipid"], answer: 2, explanation: "Carbohydrates (polysaccharides like starch, glycogen, and cellulose) are polymers of monosaccharide monomers connected by glycosidic bonds formed through dehydration synthesis." },
  { id: "b68", subject: "biology", topic: "Biochemistry", question: "A competitive inhibitor reduces enzyme activity by:", choices: ["Binding to the allosteric site and changing enzyme shape", "Binding to the active site and blocking substrate entry", "Permanently denaturing the enzyme", "Increasing the Km without affecting Vmax"], answer: 1, explanation: "A competitive inhibitor resembles the substrate and binds reversibly to the active site, blocking substrate access. It increases apparent Km but does not affect Vmax." },
  { id: "b69", subject: "biology", topic: "Biochemistry", question: "Amino acids are linked together by what type of bond to form proteins?", choices: ["Glycosidic bond", "Phosphodiester bond", "Peptide bond", "Hydrogen bond"], answer: 2, explanation: "Peptide bonds form between the carboxyl group of one amino acid and the amino group of the next through a dehydration (condensation) reaction." },
  { id: "b70", subject: "biology", topic: "Biochemistry", question: "Which type of lipid forms the structural basis of biological membranes?", choices: ["Triglycerides", "Phospholipids", "Steroids", "Waxes"], answer: 1, explanation: "Phospholipids have hydrophilic head groups and hydrophobic fatty acid tails, causing them to spontaneously form bilayers in aqueous environments — the structural foundation of all biological membranes." },

  // Plant Biology
  { id: "b71", subject: "biology", topic: "Plant Biology", question: "Water is pulled upward through xylem vessels primarily by:", choices: ["Root pressure alone", "Capillary action alone", "Transpiration-cohesion-tension mechanism", "Active transport by xylem cells"], answer: 2, explanation: "The cohesion-tension theory explains that transpiration creates negative pressure that pulls water upward through xylem, aided by cohesion between water molecules via hydrogen bonding." },
  { id: "b72", subject: "biology", topic: "Plant Biology", question: "Guard cells regulate gas exchange in leaves by controlling the opening and closing of:", choices: ["Lenticels", "Stomata", "Plasmodesmata", "Trichomes"], answer: 1, explanation: "Stomata are pores flanked by two guard cells. When guard cells absorb water and become turgid, the stoma opens. When they lose water, the stoma closes to reduce water loss." },
  { id: "b73", subject: "biology", topic: "Plant Biology", question: "Auxin promotes cell elongation and is primarily produced in:", choices: ["Root tips", "Apical meristems and young leaves", "Mature bark tissue", "Flower petals"], answer: 1, explanation: "Auxin (primarily indoleacetic acid, IAA) is synthesized mainly in apical meristems, young leaves, and developing seeds. It promotes cell elongation by loosening cell wall cross-links." },
  { id: "b74", subject: "biology", topic: "Plant Biology", question: "A plant stem bending toward a light source demonstrates:", choices: ["Gravitropism", "Phototropism", "Thigmotropism", "Hydrotropism"], answer: 1, explanation: "Phototropism is directional growth toward or away from light. Auxin accumulates on the shaded side of the stem, causing those cells to elongate more, bending the stem toward the light." },
  { id: "b75", subject: "biology", topic: "Plant Biology", question: "In angiosperms, double fertilization produces:", choices: ["Two identical embryos", "An embryo and a triploid endosperm", "Two seeds from one ovule", "A haploid embryo and diploid endosperm"], answer: 1, explanation: "In double fertilization, one sperm fuses with the egg to form a diploid zygote (embryo), while the second sperm fuses with two polar nuclei to form a triploid (3n) endosperm that nourishes the developing embryo." },

  // ═══════════════════════════════════════════
  //  CHEMISTRY
  // ═══════════════════════════════════════════

  // Atomic Structure
  { id: "c1", subject: "chemistry", topic: "Atomic Structure", question: "How many electrons can the third energy level (n=3) hold?", choices: ["2", "8", "18", "32"], answer: 2, explanation: "The maximum number of electrons in an energy level is 2n². For n=3: 2(3²) = 2(9) = 18 electrons." },
  { id: "c2", subject: "chemistry", topic: "Atomic Structure", question: "Which quantum number describes the shape of an orbital?", choices: ["Principal (n)", "Angular momentum (ℓ)", "Magnetic (mℓ)", "Spin (ms)"], answer: 1, explanation: "The angular momentum quantum number (ℓ) determines the shape: ℓ=0 is s (sphere), ℓ=1 is p (dumbbell), ℓ=2 is d, ℓ=3 is f." },
  { id: "c3", subject: "chemistry", topic: "Atomic Structure", question: "Isotopes of an element differ in their number of:", choices: ["Protons", "Electrons", "Neutrons", "Valence electrons"], answer: 2, explanation: "Isotopes have the same number of protons but different numbers of neutrons, giving them different mass numbers." },

  // Periodic Table
  { id: "c4", subject: "chemistry", topic: "Periodic Table", question: "As you move from left to right across a period, atomic radius generally:", choices: ["Increases", "Decreases", "Remains the same", "Fluctuates randomly"], answer: 1, explanation: "Across a period, increasing nuclear charge pulls electrons closer, decreasing the atomic radius despite adding electrons to the same shell." },
  { id: "c5", subject: "chemistry", topic: "Periodic Table", question: "Which group of elements has a full valence shell and is generally unreactive?", choices: ["Alkali metals", "Halogens", "Noble gases", "Transition metals"], answer: 2, explanation: "Noble gases (Group 18) have full valence electron shells, making them very stable and generally unreactive." },
  { id: "c6", subject: "chemistry", topic: "Periodic Table", question: "Electronegativity generally increases in which direction on the periodic table?", choices: ["Down and to the left", "Up and to the right", "Down and to the right", "Up and to the left"], answer: 1, explanation: "Electronegativity increases going up (smaller atoms hold electrons more tightly) and to the right (more protons, stronger pull). Fluorine has the highest electronegativity." },

  // Chemical Bonding
  { id: "c7", subject: "chemistry", topic: "Chemical Bonding", question: "A bond formed by the sharing of electrons between two nonmetal atoms is called a:", choices: ["Ionic bond", "Covalent bond", "Metallic bond", "Hydrogen bond"], answer: 1, explanation: "Covalent bonds form when two nonmetal atoms share electron pairs to achieve stable electron configurations." },
  { id: "c8", subject: "chemistry", topic: "Chemical Bonding", question: "According to VSEPR theory, a molecule with four bonding pairs and no lone pairs has what geometry?", choices: ["Linear", "Trigonal planar", "Tetrahedral", "Trigonal bipyramidal"], answer: 2, explanation: "Four electron groups with no lone pairs arrange in a tetrahedral geometry with bond angles of approximately 109.5°." },
  { id: "c9", subject: "chemistry", topic: "Chemical Bonding", question: "Which intermolecular force is the strongest?", choices: ["London dispersion forces", "Dipole-dipole interactions", "Hydrogen bonding", "Ion-dipole forces"], answer: 3, explanation: "Ion-dipole forces (between an ion and a polar molecule) are the strongest intermolecular forces, followed by hydrogen bonding." },

  // Reactions & Stoichiometry
  { id: "c10", subject: "chemistry", topic: "Reactions", question: "In the reaction 2H₂ + O₂ → 2H₂O, how many moles of water are produced from 4 moles of H₂?", choices: ["2 moles", "4 moles", "6 moles", "8 moles"], answer: 1, explanation: "The ratio is 2 mol H₂ : 2 mol H₂O (or 1:1). So 4 mol H₂ produces 4 mol H₂O." },
  { id: "c11", subject: "chemistry", topic: "Reactions", question: "A reaction that absorbs heat from its surroundings is classified as:", choices: ["Exothermic", "Endothermic", "Isothermal", "Adiabatic"], answer: 1, explanation: "Endothermic reactions absorb heat (positive ΔH). The products have more energy than the reactants." },
  { id: "c12", subject: "chemistry", topic: "Reactions", question: "What is the oxidation state of sulfur in H₂SO₄?", choices: ["+2", "+4", "+6", "-2"], answer: 2, explanation: "In H₂SO₄: H is +1 (×2 = +2), O is -2 (×4 = -8). For the compound to be neutral: +2 + S + (-8) = 0, so S = +6." },

  // Acids & Bases
  { id: "c13", subject: "chemistry", topic: "Acids & Bases", question: "A solution with a pH of 3 has what hydrogen ion concentration?", choices: ["1 × 10⁻¹ M", "1 × 10⁻³ M", "1 × 10⁻⁷ M", "1 × 10⁻¹⁴ M"], answer: 1, explanation: "pH = -log[H⁺]. A pH of 3 means [H⁺] = 10⁻³ = 1 × 10⁻³ M." },
  { id: "c14", subject: "chemistry", topic: "Acids & Bases", question: "Which of the following is a strong acid?", choices: ["Acetic acid (CH₃COOH)", "Hydrochloric acid (HCl)", "Carbonic acid (H₂CO₃)", "Phosphoric acid (H₃PO₄)"], answer: 1, explanation: "HCl is one of the seven strong acids that completely dissociate in water. The others listed are weak acids." },
  { id: "c15", subject: "chemistry", topic: "Acids & Bases", question: "A buffer solution resists changes in pH because it contains:", choices: ["A strong acid and a strong base", "A weak acid and its conjugate base", "Two strong acids", "Pure water and a salt"], answer: 1, explanation: "Buffers contain a weak acid/base pair. The weak acid neutralizes added base, and the conjugate base neutralizes added acid." },

  // Gas Laws
  { id: "c16", subject: "chemistry", topic: "Gas Laws", question: "At STP (standard temperature and pressure), one mole of an ideal gas occupies:", choices: ["11.2 L", "22.4 L", "44.8 L", "1.00 L"], answer: 1, explanation: "At STP (0°C, 1 atm), one mole of any ideal gas occupies 22.4 liters (molar volume)." },
  { id: "c17", subject: "chemistry", topic: "Gas Laws", question: "According to Boyle's Law, if pressure on a gas doubles (at constant temperature), the volume:", choices: ["Doubles", "Is halved", "Remains the same", "Quadruples"], answer: 1, explanation: "Boyle's Law: P₁V₁ = P₂V₂. At constant temperature, pressure and volume are inversely proportional. Double pressure → half volume." },

  // Solutions
  { id: "c18", subject: "chemistry", topic: "Solutions", question: "What is the molarity of a solution containing 2 moles of NaCl dissolved in 500 mL of solution?", choices: ["1 M", "2 M", "4 M", "0.5 M"], answer: 2, explanation: "Molarity = moles of solute / liters of solution = 2 mol / 0.5 L = 4 M." },
  { id: "c19", subject: "chemistry", topic: "Solutions", question: "Adding a solute to a solvent will cause the boiling point to:", choices: ["Decrease", "Increase", "Remain the same", "Become unpredictable"], answer: 1, explanation: "Boiling point elevation is a colligative property. Adding solute particles raises the boiling point because more energy is needed to vaporize the solvent." },
  { id: "c20", subject: "chemistry", topic: "Solutions", question: "Which factor does NOT affect the rate of dissolving a solid solute?", choices: ["Stirring", "Temperature", "Surface area of solute", "Color of solute"], answer: 3, explanation: "Color is a physical property that does not affect dissolving rate. Stirring, temperature, and surface area all increase dissolution rate." },

  // Atomic Structure (continued)
  { id: "c21", subject: "chemistry", topic: "Atomic Structure", question: "What is the electron configuration of a neutral chromium (Cr, Z=24) atom in its ground state?", choices: ["[Ar] 3d⁴ 4s²", "[Ar] 3d⁵ 4s¹", "[Ar] 3d⁶", "[Ar] 3d³ 4s² 4p¹"], answer: 1, explanation: "Chromium is an exception to the Aufbau principle. A half-filled 3d subshell (3d⁵) provides extra stability, so one electron is promoted from 4s to 3d, giving [Ar] 3d⁵ 4s¹." },
  { id: "c22", subject: "chemistry", topic: "Atomic Structure", question: "Which set of quantum numbers (n, l, mₗ, mₛ) is NOT allowed for an electron?", choices: ["(3, 2, −1, +½)", "(2, 1, 0, −½)", "(1, 1, 0, +½)", "(4, 0, 0, +½)"], answer: 2, explanation: "For n=1, the only allowed value of l is 0. l=1 requires n≥2, so (1, 1, 0, +½) violates the rule that l must be less than n." },
  { id: "c23", subject: "chemistry", topic: "Atomic Structure", question: "According to Hund's rule, how are electrons distributed among the three 2p orbitals of nitrogen (Z=7)?", choices: ["All three electrons are in one orbital", "Two electrons pair in one orbital, one in another", "One electron in each orbital, all with parallel spins", "One electron in each orbital, with alternating spins"], answer: 2, explanation: "Hund's rule states that electrons fill degenerate orbitals singly with parallel spins before pairing. Nitrogen has three 2p electrons, so each 2p orbital gets one electron, all spin-up." },
  { id: "c24", subject: "chemistry", topic: "Atomic Structure", question: "Which subshell designation corresponds to the quantum numbers n=3, l=2?", choices: ["3s", "3p", "3d", "3f"], answer: 2, explanation: "The angular momentum quantum number l=0 corresponds to s, l=1 to p, l=2 to d, and l=3 to f. Therefore n=3, l=2 designates the 3d subshell." },
  { id: "c25", subject: "chemistry", topic: "Atomic Structure", question: "What is the maximum number of electrons that can occupy the 4f subshell?", choices: ["6", "10", "14", "18"], answer: 2, explanation: "The f subshell has 7 orbitals (mₗ = −3 to +3), and each orbital holds 2 electrons, so the maximum is 7 × 2 = 14 electrons." },

  // Periodic Table (continued)
  { id: "c26", subject: "chemistry", topic: "Periodic Table", question: "Which of the following correctly ranks first ionization energy from lowest to highest?", choices: ["Na < Mg < Al < Si", "Na < Al < Mg < Si", "Al < Na < Si < Mg", "Si < Mg < Al < Na"], answer: 1, explanation: "Generally, ionization energy increases across a period, but Al (removing a 3p electron) has a slightly lower ionization energy than Mg (removing a paired 3s electron). The correct order is Na < Al < Mg < Si." },
  { id: "c27", subject: "chemistry", topic: "Periodic Table", question: "Which element has the highest electron affinity (most negative value) among the following?", choices: ["Fluorine", "Chlorine", "Bromine", "Iodine"], answer: 1, explanation: "Chlorine has the most negative electron affinity. Fluorine, despite being more electronegative, has a smaller electron affinity because its small 2p orbitals cause strong electron-electron repulsion." },
  { id: "c28", subject: "chemistry", topic: "Periodic Table", question: "Which trend correctly describes metallic character in the periodic table?", choices: ["Increases left to right and top to bottom", "Decreases left to right and increases top to bottom", "Increases left to right and decreases top to bottom", "Decreases in both directions from the upper right corner"], answer: 1, explanation: "Metallic character increases going down a group (electrons are farther from the nucleus and more easily lost) and decreases going left to right across a period." },
  { id: "c29", subject: "chemistry", topic: "Periodic Table", question: "Which atom has the largest effective nuclear charge experienced by its valence electrons?", choices: ["Na", "Mg", "Al", "Cl"], answer: 3, explanation: "Effective nuclear charge (Zeff) generally increases across a period. Chlorine, farthest right in period 3 among these choices, has the highest Zeff for its valence electrons." },

  // Chemical Bonding (continued)
  { id: "c30", subject: "chemistry", topic: "Chemical Bonding", question: "What is the molecular geometry of SF₄ according to VSEPR theory?", choices: ["Tetrahedral", "Square planar", "Seesaw (distorted tetrahedron)", "Trigonal pyramidal"], answer: 2, explanation: "SF₄ has 4 bonding pairs and 1 lone pair on sulfur, giving an electron geometry of trigonal bipyramidal. With one equatorial lone pair, the molecular geometry is seesaw." },
  { id: "c31", subject: "chemistry", topic: "Chemical Bonding", question: "What hybridization is exhibited by the central carbon in carbon dioxide (CO₂)?", choices: ["sp", "sp²", "sp³", "sp³d"], answer: 0, explanation: "CO₂ is a linear molecule with two double bonds. The central carbon forms two sigma bonds and has no lone pairs, requiring sp hybridization." },
  { id: "c32", subject: "chemistry", topic: "Chemical Bonding", question: "How many sigma (σ) and pi (π) bonds are present in a molecule of acetylene (C₂H₂)?", choices: ["2σ, 2π", "3σ, 2π", "3σ, 3π", "5σ, 0π"], answer: 1, explanation: "Acetylene has a triple bond between the two carbons (1σ + 2π) and one C–H single bond on each end (1σ each). Total: 3σ bonds and 2π bonds." },
  { id: "c33", subject: "chemistry", topic: "Chemical Bonding", question: "Which of the following molecules is polar?", choices: ["CO₂", "BF₃", "CH₂Cl₂", "CCl₄"], answer: 2, explanation: "CH₂Cl₂ has a tetrahedral geometry but with two different types of atoms bonded to carbon, so the bond dipoles do not cancel. CO₂, BF₃, and CCl₄ are all symmetric and nonpolar." },
  { id: "c34", subject: "chemistry", topic: "Chemical Bonding", question: "What is the formal charge on the nitrogen atom in the nitrate ion (NO₃⁻)?", choices: ["+1", "0", "−1", "+2"], answer: 0, explanation: "In the Lewis structure of NO₃⁻, nitrogen has 5 valence electrons, 0 lone pair electrons, and shares 4 bonds. Formal charge = 5 − 0 − 4 = +1." },

  // Reactions & Stoichiometry (continued)
  { id: "c35", subject: "chemistry", topic: "Reactions", question: "In the reaction 2Al + 3Cl₂ → 2AlCl₃, if 2.7 g of Al reacts with 21.3 g of Cl₂, which is the limiting reagent? (Al = 27 g/mol, Cl₂ = 71 g/mol)", choices: ["Al", "Cl₂", "AlCl₃", "Neither, they are in stoichiometric ratio"], answer: 0, explanation: "Moles of Al = 2.7/27 = 0.10 mol. Moles of Cl₂ = 21.3/71 = 0.30 mol. For 0.10 mol Al, we need 0.15 mol Cl₂, but we have 0.30 mol — Cl₂ is in excess. Al is the limiting reagent." },
  { id: "c36", subject: "chemistry", topic: "Reactions", question: "A student produces 15.0 g of water from a combustion reaction. If the theoretical yield is 18.0 g, what is the percent yield?", choices: ["120%", "83.3%", "75.0%", "66.7%"], answer: 1, explanation: "Percent yield = (actual yield / theoretical yield) × 100 = (15.0/18.0) × 100 = 83.3%." },
  { id: "c37", subject: "chemistry", topic: "Reactions", question: "In the reaction Zn + CuSO₄ → ZnSO₄ + Cu, which species is oxidized?", choices: ["Cu²⁺", "Zn", "SO₄²⁻", "Cu"], answer: 1, explanation: "Zinc goes from an oxidation state of 0 to +2 (loses electrons), so it is oxidized. Cu²⁺ is reduced from +2 to 0." },
  { id: "c38", subject: "chemistry", topic: "Reactions", question: "What type of reaction is: 2KClO₃ → 2KCl + 3O₂?", choices: ["Synthesis", "Single replacement", "Decomposition", "Double replacement"], answer: 2, explanation: "A single reactant breaks down into two or more simpler products. KClO₃ decomposes into KCl and O₂, which is a classic decomposition reaction." },
  { id: "c39", subject: "chemistry", topic: "Reactions", question: "What is the net ionic equation for the reaction between aqueous NaCl and aqueous AgNO₃?", choices: ["Na⁺ + NO₃⁻ → NaNO₃", "Ag⁺(aq) + Cl⁻(aq) → AgCl(s)", "NaCl + AgNO₃ → AgCl + NaNO₃", "Ag⁺ + Na⁺ → AgNa²⁺"], answer: 1, explanation: "AgCl is insoluble and precipitates out. The spectator ions (Na⁺ and NO₃⁻) are removed, leaving the net ionic equation: Ag⁺(aq) + Cl⁻(aq) → AgCl(s)." },
  { id: "c40", subject: "chemistry", topic: "Reactions", question: "What coefficient is placed in front of O₂ when the equation C₃H₈ + O₂ → CO₂ + H₂O is balanced?", choices: ["3", "4", "5", "10"], answer: 2, explanation: "Balanced equation: C₃H₈ + 5O₂ → 3CO₂ + 4H₂O. Oxygen: 6 from CO₂ + 4 from H₂O = 10, and 5×2 = 10." },

  // Acids & Bases (continued)
  { id: "c41", subject: "chemistry", topic: "Acids & Bases", question: "What volume of 0.200 M NaOH is needed to neutralize 25.0 mL of 0.150 M HCl?", choices: ["12.5 mL", "18.75 mL", "25.0 mL", "33.3 mL"], answer: 1, explanation: "HCl + NaOH → NaCl + H₂O is a 1:1 reaction. Using M₁V₁ = M₂V₂: (0.150)(25.0) = (0.200)(V₂). V₂ = 3.75/0.200 = 18.75 mL." },
  { id: "c42", subject: "chemistry", topic: "Acids & Bases", question: "A weak acid HA has a Ka of 1.8 × 10⁻⁵. What is the pKa of this acid?", choices: ["4.74", "5.00", "3.25", "9.26"], answer: 0, explanation: "pKa = −log(Ka) = −log(1.8 × 10⁻⁵) ≈ 4.74. This is the pKa of acetic acid." },
  { id: "c43", subject: "chemistry", topic: "Acids & Bases", question: "A buffer solution contains 0.10 M acetic acid (pKa = 4.74) and 0.10 M sodium acetate. What is the pH?", choices: ["4.74", "7.00", "9.26", "3.74"], answer: 0, explanation: "Using the Henderson-Hasselbalch equation: pH = pKa + log([A⁻]/[HA]) = 4.74 + log(0.10/0.10) = 4.74 + 0 = 4.74." },
  { id: "c44", subject: "chemistry", topic: "Acids & Bases", question: "Which indicator would be most appropriate for a strong acid–strong base titration?", choices: ["Methyl orange (pH 3.1–4.4)", "Bromothymol blue (pH 6.0–7.6)", "Phenolphthalein (pH 8.2–10.0)", "Any of these would work"], answer: 3, explanation: "For a strong acid–strong base titration, the pH changes sharply from about pH 3 to pH 11 at the equivalence point. Any indicator that changes color within this range will work." },

  // Gas Laws (continued)
  { id: "c45", subject: "chemistry", topic: "Gas Laws", question: "A sample of gas occupies 2.00 L at 300 K and 1.00 atm. What volume does it occupy at 600 K and 2.00 atm?", choices: ["1.00 L", "2.00 L", "4.00 L", "8.00 L"], answer: 1, explanation: "Using the combined gas law: P₁V₁/T₁ = P₂V₂/T₂. (1.00)(2.00)/300 = (2.00)(V₂)/600. V₂ = 2.00 L. Doubling temperature and pressure cancel each other out." },
  { id: "c46", subject: "chemistry", topic: "Gas Laws", question: "A mixture contains 2.0 mol N₂ and 3.0 mol O₂ at a total pressure of 5.0 atm. What is the partial pressure of N₂?", choices: ["1.0 atm", "2.0 atm", "3.0 atm", "2.5 atm"], answer: 1, explanation: "By Dalton's law: P(N₂) = χ(N₂) × P(total). Mole fraction of N₂ = 2.0/(2.0+3.0) = 0.40. P(N₂) = 0.40 × 5.0 = 2.0 atm." },
  { id: "c47", subject: "chemistry", topic: "Gas Laws", question: "According to Graham's law, what is the ratio of the effusion rate of H₂ to O₂? (H₂ = 2 g/mol, O₂ = 32 g/mol)", choices: ["4:1", "16:1", "2:1", "1:4"], answer: 0, explanation: "Graham's law: rate₁/rate₂ = √(M₂/M₁). Rate(H₂)/Rate(O₂) = √(32/2) = √16 = 4. H₂ effuses 4 times faster than O₂." },
  { id: "c48", subject: "chemistry", topic: "Gas Laws", question: "How many moles of gas are in a 10.0 L vessel at 2.00 atm and 273 K? (R = 0.0821 L·atm/mol·K)", choices: ["0.446 mol", "0.893 mol", "1.12 mol", "2.24 mol"], answer: 1, explanation: "Using PV = nRT: n = PV/RT = (2.00 × 10.0)/(0.0821 × 273) = 20.0/22.41 = 0.893 mol." },

  // Solutions (continued)
  { id: "c49", subject: "chemistry", topic: "Solutions", question: "What is the molarity of a solution prepared by diluting 50.0 mL of 6.00 M HCl to 500.0 mL?", choices: ["0.300 M", "0.600 M", "3.00 M", "60.0 M"], answer: 1, explanation: "Using M₁V₁ = M₂V₂: (6.00)(50.0) = M₂(500.0). M₂ = 300/500.0 = 0.600 M." },
  { id: "c50", subject: "chemistry", topic: "Solutions", question: "Which of the following compounds is INSOLUBLE in water?", choices: ["NaCl", "KNO₃", "BaSO₄", "NH₄Cl"], answer: 2, explanation: "According to solubility rules, most sulfates are soluble, but BaSO₄ is a well-known exception. Barium sulfate is insoluble." },
  { id: "c51", subject: "chemistry", topic: "Solutions", question: "Adding a nonvolatile solute to water causes:", choices: ["Boiling point decrease and freezing point increase", "Boiling point increase and freezing point decrease", "Both boiling point and freezing point increase", "Both boiling point and freezing point decrease"], answer: 1, explanation: "Colligative properties dictate that a nonvolatile solute elevates the boiling point and lowers the freezing point of the solvent." },

  // Thermochemistry
  { id: "c52", subject: "chemistry", topic: "Thermochemistry", question: "The combustion of methane releases 890 kJ/mol. What is ΔH for combustion of 2 moles?", choices: ["+1780 kJ", "−890 kJ", "−1780 kJ", "+890 kJ"], answer: 2, explanation: "Combustion is exothermic, so ΔH is negative. For 2 moles: ΔH = 2 × (−890) = −1780 kJ." },
  { id: "c53", subject: "chemistry", topic: "Thermochemistry", question: "Using Hess's Law, given: C + O₂ → CO₂ (ΔH = −393 kJ) and CO + ½O₂ → CO₂ (ΔH = −283 kJ), what is ΔH for C + ½O₂ → CO?", choices: ["−110 kJ", "+110 kJ", "−676 kJ", "−283 kJ"], answer: 0, explanation: "By Hess's Law: ΔH = −393 − (−283) = −393 + 283 = −110 kJ." },
  { id: "c54", subject: "chemistry", topic: "Thermochemistry", question: "A 50.0 g sample of water absorbs 4180 J of heat. What is the temperature change? (c = 4.18 J/g·°C)", choices: ["10°C", "20°C", "50°C", "100°C"], answer: 1, explanation: "Using q = mcΔT: ΔT = q/(mc) = 4180/(50.0 × 4.18) = 4180/209 = 20.0°C." },
  { id: "c55", subject: "chemistry", topic: "Thermochemistry", question: "Estimate ΔH for H₂ + Cl₂ → 2HCl using bond energies: H–H = 436, Cl–Cl = 242, H–Cl = 431 kJ/mol.", choices: ["−184 kJ", "+184 kJ", "−625 kJ", "−431 kJ"], answer: 0, explanation: "ΔH = Σ(bonds broken) − Σ(bonds formed) = (436 + 242) − (2 × 431) = 678 − 862 = −184 kJ. The reaction is exothermic." },
  { id: "c56", subject: "chemistry", topic: "Thermochemistry", question: "Which process has a positive entropy change (ΔS > 0)?", choices: ["Water freezing to ice", "Gas compressed into a smaller volume", "A solid dissolving in water to form ions", "Water vapor condensing to liquid"], answer: 2, explanation: "Dissolving a solid into ions in solution increases disorder (entropy). Freezing, compression, and condensation all decrease entropy." },

  // Kinetics
  { id: "c57", subject: "chemistry", topic: "Kinetics", question: "For Rate = k[A]²[B], what happens to the rate if [A] is doubled while [B] is constant?", choices: ["Rate doubles", "Rate triples", "Rate quadruples", "Rate increases 8 times"], answer: 2, explanation: "The rate depends on [A]². Doubling [A] gives (2)² = 4 times the original rate." },
  { id: "c58", subject: "chemistry", topic: "Kinetics", question: "A catalyst increases the rate of a chemical reaction by:", choices: ["Increasing the enthalpy of the reaction", "Lowering the activation energy", "Increasing the concentration of reactants", "Shifting the equilibrium toward products"], answer: 1, explanation: "A catalyst provides an alternative reaction pathway with a lower activation energy. It does not change the thermodynamics or equilibrium position." },
  { id: "c59", subject: "chemistry", topic: "Kinetics", question: "In a multi-step reaction mechanism, the rate-determining step is:", choices: ["The fastest elementary step", "The slowest elementary step", "The step with the most reactants", "Always the first step"], answer: 1, explanation: "The rate-determining step is the slowest step in a reaction mechanism. It acts as a bottleneck and determines the overall rate of the reaction." },
  { id: "c60", subject: "chemistry", topic: "Kinetics", question: "The half-life of a first-order reaction is 20 minutes. What fraction of reactant remains after 60 minutes?", choices: ["1/2", "1/4", "1/8", "1/16"], answer: 2, explanation: "60 minutes = 3 half-lives. Fraction remaining = (1/2)³ = 1/8." },

  // Equilibrium
  { id: "c61", subject: "chemistry", topic: "Equilibrium", question: "For N₂(g) + 3H₂(g) ⇌ 2NH₃(g), which change shifts equilibrium toward more NH₃?", choices: ["Removing N₂", "Increasing the volume", "Increasing pressure by decreasing volume", "Adding a catalyst"], answer: 2, explanation: "Increasing pressure shifts equilibrium toward fewer moles of gas. Products have 2 moles vs. 4 for reactants, so equilibrium shifts toward NH₃." },
  { id: "c62", subject: "chemistry", topic: "Equilibrium", question: "At a certain temperature, A ⇌ 2B has Keq = 4.0. If [A]₀ = 1.0 M and [B]₀ = 0, what is [B] at equilibrium?", choices: ["0.5 M", "1.0 M", "1.24 M", "2.0 M"], answer: 2, explanation: "ICE table: Keq = (2x)²/(1.0−x) = 4.0. Solving: 4x² + 4x − 4 = 0 → x ≈ 0.618. [B] = 2x ≈ 1.24 M." },
  { id: "c63", subject: "chemistry", topic: "Equilibrium", question: "The Ksp of AgCl is 1.8 × 10⁻¹⁰. What is the molar solubility of AgCl in pure water?", choices: ["1.8 × 10⁻¹⁰ M", "1.3 × 10⁻⁵ M", "9.0 × 10⁻⁶ M", "1.8 × 10⁻⁵ M"], answer: 1, explanation: "AgCl → Ag⁺ + Cl⁻. Ksp = s² → s = √(1.8 × 10⁻¹⁰) = 1.34 × 10⁻⁵ M." },
  { id: "c64", subject: "chemistry", topic: "Equilibrium", question: "What is the effect of adding NaCl to a saturated solution of AgCl?", choices: ["More AgCl dissolves", "AgCl precipitates out", "No effect", "The Ksp of AgCl increases"], answer: 1, explanation: "This is the common ion effect. Adding Cl⁻ ions increases [Cl⁻], causing the ion product to exceed Ksp. AgCl precipitates to restore equilibrium." },
  { id: "c65", subject: "chemistry", topic: "Equilibrium", question: "For an endothermic reaction at equilibrium, increasing the temperature will:", choices: ["Shift toward reactants and decrease Keq", "Shift toward products and increase Keq", "Have no effect on equilibrium", "Only increase the rate"], answer: 1, explanation: "For an endothermic reaction, heat is a reactant. Increasing temperature shifts equilibrium toward products and increases Keq." },

  // Electrochemistry
  { id: "c66", subject: "chemistry", topic: "Electrochemistry", question: "In a galvanic (voltaic) cell, oxidation occurs at which electrode?", choices: ["Cathode", "Anode", "Salt bridge", "Both electrodes"], answer: 1, explanation: "In any electrochemical cell, oxidation occurs at the anode. In a galvanic cell, the anode is the negative electrode." },
  { id: "c67", subject: "chemistry", topic: "Electrochemistry", question: "Given E°: Cu²⁺/Cu = +0.34 V, Zn²⁺/Zn = −0.76 V. What is E°cell for a Zn–Cu galvanic cell?", choices: ["0.42 V", "1.10 V", "−1.10 V", "−0.42 V"], answer: 1, explanation: "E°cell = E°cathode − E°anode = (+0.34) − (−0.76) = +1.10 V." },
  { id: "c68", subject: "chemistry", topic: "Electrochemistry", question: "In electrolysis of molten NaCl, what is produced at the cathode?", choices: ["Chlorine gas", "Sodium metal", "Sodium chloride", "Oxygen gas"], answer: 1, explanation: "Reduction occurs at the cathode. Na⁺ ions are reduced to sodium metal (Na⁺ + e⁻ → Na)." },
  { id: "c69", subject: "chemistry", topic: "Electrochemistry", question: "As a galvanic cell approaches equilibrium, its cell potential:", choices: ["Increases", "Stays constant", "Decreases toward zero", "Becomes negative"], answer: 2, explanation: "As reactants are consumed, Q increases. The Nernst equation shows E decreases. At equilibrium, Q = K and E = 0." },

  // Nuclear Chemistry
  { id: "c70", subject: "chemistry", topic: "Nuclear Chemistry", question: "Uranium-238 undergoes alpha decay. What is the daughter nucleus?", choices: ["Th-234", "Pa-234", "U-234", "Ra-234"], answer: 0, explanation: "Alpha decay emits a helium-4 nucleus. ²³⁸U → ⁴He + ²³⁴Th. The daughter is thorium-234." },
  { id: "c71", subject: "chemistry", topic: "Nuclear Chemistry", question: "A radioactive isotope has a half-life of 10 years. Starting with 80 g, how much remains after 30 years?", choices: ["40 g", "20 g", "10 g", "5 g"], answer: 2, explanation: "30 years = 3 half-lives. 80 → 40 → 20 → 10 g." },
  { id: "c72", subject: "chemistry", topic: "Nuclear Chemistry", question: "Which radioactive decay involves converting a neutron into a proton?", choices: ["Alpha decay", "Beta-minus (β⁻) decay", "Beta-plus (β⁺) decay", "Gamma emission"], answer: 1, explanation: "In beta-minus decay, a neutron converts to a proton, emitting an electron (beta particle) and an antineutrino. The atomic number increases by 1." },

  // Organic Chemistry
  { id: "c73", subject: "chemistry", topic: "Organic Chemistry", question: "What is the IUPAC name for CH₃CH₂CH₂OH?", choices: ["Methanol", "Ethanol", "1-Propanol", "2-Propanol"], answer: 2, explanation: "A 3-carbon chain with a hydroxyl group on the terminal carbon. The IUPAC name is 1-propanol." },
  { id: "c74", subject: "chemistry", topic: "Organic Chemistry", question: "Which functional group is present in CH₃COCH₃ (acetone)?", choices: ["Aldehyde", "Ketone", "Carboxylic acid", "Ester"], answer: 1, explanation: "Acetone has a carbonyl group (C=O) bonded to two carbon-containing groups (two methyl groups), defining a ketone." },
  { id: "c75", subject: "chemistry", topic: "Organic Chemistry", question: "Butane and 2-methylpropane are examples of what type of isomers?", choices: ["Geometric isomers", "Optical isomers", "Structural (constitutional) isomers", "Conformational isomers"], answer: 2, explanation: "Both have molecular formula C₄H₁₀ but different connectivity of atoms, making them structural (constitutional) isomers." },

  // ═══════════════════════════════════════════
  //  PHYSICS
  // ═══════════════════════════════════════════

  // Mechanics
  { id: "p1", subject: "physics", topic: "Mechanics", question: "A car accelerates from rest at 3 m/s². What is its velocity after 5 seconds?", choices: ["8 m/s", "15 m/s", "25 m/s", "30 m/s"], answer: 1, explanation: "v = v₀ + at = 0 + (3)(5) = 15 m/s." },
  { id: "p2", subject: "physics", topic: "Mechanics", question: "An object is in free fall near Earth's surface. Ignoring air resistance, its acceleration is approximately:", choices: ["5.0 m/s²", "9.8 m/s²", "15.0 m/s²", "32.2 m/s²"], answer: 1, explanation: "The acceleration due to gravity near Earth's surface is approximately 9.8 m/s² (or ~10 m/s² for estimation). 32.2 ft/s² is the value in imperial units." },
  { id: "p3", subject: "physics", topic: "Mechanics", question: "According to Newton's Third Law, when you push on a wall, the wall:", choices: ["Does not push back", "Pushes back with less force", "Pushes back with equal and opposite force", "Pushes back with greater force"], answer: 2, explanation: "Newton's Third Law: For every action, there is an equal and opposite reaction. The wall pushes back on you with the same magnitude of force." },
  { id: "p4", subject: "physics", topic: "Mechanics", question: "What is the net force on a 5 kg object accelerating at 4 m/s²?", choices: ["1.25 N", "9 N", "20 N", "80 N"], answer: 2, explanation: "Newton's Second Law: F = ma = (5 kg)(4 m/s²) = 20 N." },

  // Energy & Work
  { id: "p5", subject: "physics", topic: "Energy", question: "A 2 kg ball is held 10 m above the ground. What is its gravitational potential energy? (g = 10 m/s²)", choices: ["20 J", "100 J", "200 J", "2000 J"], answer: 2, explanation: "PE = mgh = (2 kg)(10 m/s²)(10 m) = 200 J." },
  { id: "p6", subject: "physics", topic: "Energy", question: "If a force of 50 N moves an object 4 m in the direction of the force, how much work is done?", choices: ["12.5 J", "46 J", "54 J", "200 J"], answer: 3, explanation: "Work = Force × distance × cos(θ). When force and displacement are in the same direction: W = (50 N)(4 m) = 200 J." },
  { id: "p7", subject: "physics", topic: "Energy", question: "The kinetic energy of an object depends on its:", choices: ["Mass only", "Velocity only", "Mass and velocity", "Mass and height"], answer: 2, explanation: "KE = ½mv². Kinetic energy depends on both mass and the square of velocity." },

  // Waves & Sound
  { id: "p8", subject: "physics", topic: "Waves", question: "If a wave has a frequency of 500 Hz and a wavelength of 0.68 m, what is its speed?", choices: ["170 m/s", "340 m/s", "500 m/s", "735 m/s"], answer: 1, explanation: "Wave speed = frequency × wavelength = (500 Hz)(0.68 m) = 340 m/s (approximately the speed of sound in air)." },
  { id: "p9", subject: "physics", topic: "Waves", question: "Which type of wave requires a medium to travel through?", choices: ["Electromagnetic waves", "Mechanical waves", "Light waves", "Radio waves"], answer: 1, explanation: "Mechanical waves (sound, water waves, seismic waves) require a medium. Electromagnetic waves can travel through a vacuum." },
  { id: "p10", subject: "physics", topic: "Waves", question: "The Doppler effect explains why:", choices: ["Light bends around objects", "Sound seems louder at night", "A siren sounds higher-pitched as it approaches", "Echoes occur in mountains"], answer: 2, explanation: "The Doppler effect describes the change in frequency/pitch of a wave as the source moves toward (higher pitch) or away from (lower pitch) an observer." },

  // Electricity
  { id: "p11", subject: "physics", topic: "Electricity", question: "According to Ohm's Law, if the voltage across a resistor is 12 V and the current is 3 A, the resistance is:", choices: ["0.25 Ω", "4 Ω", "15 Ω", "36 Ω"], answer: 1, explanation: "Ohm's Law: V = IR, so R = V/I = 12 V / 3 A = 4 Ω." },
  { id: "p12", subject: "physics", topic: "Electricity", question: "In a series circuit with three resistors (2 Ω, 3 Ω, 5 Ω), the total resistance is:", choices: ["1.03 Ω", "3.33 Ω", "10 Ω", "30 Ω"], answer: 2, explanation: "In a series circuit, total resistance is the sum: R_total = 2 + 3 + 5 = 10 Ω." },
  { id: "p13", subject: "physics", topic: "Electricity", question: "Electric power is calculated by:", choices: ["P = V/I", "P = IR", "P = VI", "P = V²/I"], answer: 2, explanation: "Electric power P = VI (voltage × current). It can also be written as P = I²R or P = V²/R." },

  // Light & Optics
  { id: "p14", subject: "physics", topic: "Optics", question: "When light passes from air into glass, it bends toward the normal because glass has a:", choices: ["Lower index of refraction", "Higher index of refraction", "Lower density", "Higher wavelength"], answer: 1, explanation: "Glass has a higher index of refraction than air. Light slows down in denser media and bends toward the normal (Snell's Law)." },
  { id: "p15", subject: "physics", topic: "Optics", question: "A converging (convex) lens can produce which type of image when the object is beyond the focal point?", choices: ["Only virtual and upright", "Only real and inverted", "Only virtual and inverted", "Real and inverted, or virtual and upright"], answer: 1, explanation: "When the object is beyond the focal point of a converging lens, a real, inverted image is produced on the other side." },

  // Thermodynamics
  { id: "p16", subject: "physics", topic: "Thermodynamics", question: "Heat always flows spontaneously from:", choices: ["Cold to hot objects", "Hot to cold objects", "Low pressure to high pressure", "High density to low density"], answer: 1, explanation: "The Second Law of Thermodynamics states that heat spontaneously flows from hot to cold objects until thermal equilibrium is reached." },
  { id: "p17", subject: "physics", topic: "Thermodynamics", question: "How much heat is needed to raise the temperature of 200 g of water by 5°C? (Specific heat of water = 4.18 J/g°C)", choices: ["836 J", "1045 J", "4180 J", "20900 J"], answer: 2, explanation: "Q = mcΔT = (200 g)(4.18 J/g°C)(5°C) = 4180 J." },

  // Momentum
  { id: "p18", subject: "physics", topic: "Momentum", question: "What is the momentum of a 3 kg object moving at 8 m/s?", choices: ["2.67 kg·m/s", "11 kg·m/s", "24 kg·m/s", "192 kg·m/s"], answer: 2, explanation: "Momentum = mass × velocity = (3 kg)(8 m/s) = 24 kg·m/s." },
  { id: "p19", subject: "physics", topic: "Momentum", question: "In a perfectly inelastic collision, the two objects:", choices: ["Bounce off each other", "Stick together", "Both stop moving", "Exchange velocities"], answer: 1, explanation: "In a perfectly inelastic collision, the objects stick together after colliding and move as one combined mass. Kinetic energy is not conserved, but momentum is." },
  { id: "p20", subject: "physics", topic: "Momentum", question: "The impulse experienced by an object equals:", choices: ["Force × distance", "Mass × velocity", "Force × time", "Mass × acceleration × distance"], answer: 2, explanation: "Impulse = Force × time (J = FΔt). By the impulse-momentum theorem, impulse equals the change in momentum." },

  // Mechanics (continued)
  { id: "p21", subject: "physics", topic: "Mechanics", question: "A projectile is launched horizontally at 20 m/s from a cliff 80 m high. How long does it take to hit the ground? (g = 10 m/s²)", choices: ["2 s", "4 s", "8 s", "16 s"], answer: 1, explanation: "Vertical: h = ½gt² → 80 = ½(10)t² → t² = 16 → t = 4 s. Horizontal velocity does not affect the time to fall." },
  { id: "p22", subject: "physics", topic: "Mechanics", question: "A 1500 kg car rounds a flat curve of radius 50 m at 20 m/s. What centripetal force is required?", choices: ["6000 N", "12000 N", "15000 N", "30000 N"], answer: 1, explanation: "F_c = mv²/r = (1500)(20²)/50 = (1500)(400)/50 = 12000 N." },
  { id: "p23", subject: "physics", topic: "Mechanics", question: "A 10 kg box sits on a surface with coefficient of static friction 0.4. What is the maximum static friction force? (g = 10 m/s²)", choices: ["4 N", "25 N", "40 N", "100 N"], answer: 2, explanation: "f_s(max) = μ_s × N = μ_s × mg = (0.4)(10)(10) = 40 N." },
  { id: "p24", subject: "physics", topic: "Mechanics", question: "A 5 kg block slides down a frictionless incline at 30°. What is its acceleration? (g = 10 m/s²)", choices: ["2.5 m/s²", "5.0 m/s²", "8.7 m/s²", "10.0 m/s²"], answer: 1, explanation: "On a frictionless incline, a = g sin θ = (10) sin 30° = (10)(0.5) = 5.0 m/s²." },
  { id: "p25", subject: "physics", topic: "Mechanics", question: "A wrench 0.3 m long has a force of 40 N applied perpendicular to its end. What torque is produced?", choices: ["6 N·m", "12 N·m", "120 N·m", "133 N·m"], answer: 1, explanation: "Torque = rF sin θ = (0.3)(40)(sin 90°) = 12 N·m." },
  { id: "p26", subject: "physics", topic: "Mechanics", question: "A solid disk and a hoop of the same mass and radius roll down an incline. Which reaches the bottom first?", choices: ["The hoop", "The solid disk", "They arrive at the same time", "It depends on the angle"], answer: 1, explanation: "The solid disk has a smaller moment of inertia (½MR²) compared to the hoop (MR²), so more energy goes into translation, making it faster." },
  { id: "p27", subject: "physics", topic: "Mechanics", question: "An object moves in a circle at constant speed. The direction of its centripetal acceleration is:", choices: ["Tangent to the circle", "Away from the center", "Toward the center", "In the direction of velocity"], answer: 2, explanation: "Centripetal acceleration always points radially inward, toward the center of the circular path." },
  { id: "p28", subject: "physics", topic: "Mechanics", question: "A ball is launched at 30° above the horizontal at 40 m/s. What is the horizontal component of its initial velocity?", choices: ["20.0 m/s", "34.6 m/s", "36.0 m/s", "40.0 m/s"], answer: 1, explanation: "v_x = v cos θ = 40 cos 30° = 40 × (√3/2) ≈ 34.6 m/s." },

  // Energy (continued)
  { id: "p29", subject: "physics", topic: "Energy", question: "A 2 kg ball dropped from 20 m. What is its speed just before hitting the ground? (g = 10 m/s²)", choices: ["10 m/s", "14.1 m/s", "20 m/s", "40 m/s"], answer: 2, explanation: "mgh = ½mv² → v = √(2gh) = √(2 × 10 × 20) = √400 = 20 m/s." },
  { id: "p30", subject: "physics", topic: "Energy", question: "A motor lifts a 200 kg load 15 m in 10 seconds. What power does it deliver? (g = 10 m/s²)", choices: ["300 W", "3000 W", "30000 W", "300000 W"], answer: 1, explanation: "P = W/t = mgh/t = (200)(10)(15)/10 = 3000 W." },
  { id: "p31", subject: "physics", topic: "Energy", question: "A machine does 800 J of useful work from 1000 J of input energy. What is its efficiency?", choices: ["20%", "50%", "80%", "125%"], answer: 2, explanation: "Efficiency = (useful work output / total energy input) × 100% = (800/1000) × 100% = 80%." },
  { id: "p32", subject: "physics", topic: "Energy", question: "A spring with k = 200 N/m is compressed 0.3 m. How much elastic potential energy is stored?", choices: ["9 J", "30 J", "60 J", "90 J"], answer: 0, explanation: "PE = ½kx² = ½(200)(0.3)² = ½(200)(0.09) = 9 J." },
  { id: "p33", subject: "physics", topic: "Energy", question: "According to the work-energy theorem, net work done on an object equals its change in:", choices: ["Potential energy", "Kinetic energy", "Total mechanical energy", "Momentum"], answer: 1, explanation: "The work-energy theorem states W_net = ΔKE = ½mv_f² − ½mv_i²." },

  // Waves (continued)
  { id: "p34", subject: "physics", topic: "Waves", question: "A string fixed at both ends vibrates in its third harmonic. How many antinodes are present?", choices: ["1", "2", "3", "4"], answer: 2, explanation: "The nth harmonic of a string fixed at both ends has n antinodes. The third harmonic has 3 antinodes." },
  { id: "p35", subject: "physics", topic: "Waves", question: "Two speakers emit same-frequency sound in phase. At a point equidistant from both, what interference occurs?", choices: ["Destructive", "Constructive", "No interference", "Partial only"], answer: 1, explanation: "At a point equidistant from both sources, the path length difference is zero, so waves arrive in phase — constructive interference." },
  { id: "p36", subject: "physics", topic: "Waves", question: "Diffraction is most pronounced when the wavelength is:", choices: ["Much smaller than the opening", "Much larger than the opening", "Approximately equal to the size of the opening", "Unrelated to the opening size"], answer: 2, explanation: "Diffraction effects are most significant when the wavelength is comparable to the size of the obstacle or opening." },
  { id: "p37", subject: "physics", topic: "Waves", question: "Which electromagnetic radiation has the highest frequency?", choices: ["Microwaves", "Ultraviolet", "X-rays", "Gamma rays"], answer: 3, explanation: "In the EM spectrum, gamma rays have the highest frequency and shortest wavelength." },
  { id: "p38", subject: "physics", topic: "Waves", question: "Resonance occurs when an external force drives a system at its:", choices: ["Lowest possible frequency", "Highest possible frequency", "Natural frequency", "Any arbitrary frequency"], answer: 2, explanation: "Resonance occurs when the driving frequency matches the system's natural frequency, causing maximum amplitude." },

  // Electricity (continued)
  { id: "p39", subject: "physics", topic: "Electricity", question: "Three 6 Ω resistors are connected in parallel. What is the equivalent resistance?", choices: ["2 Ω", "6 Ω", "9 Ω", "18 Ω"], answer: 0, explanation: "1/R_eq = 1/6 + 1/6 + 1/6 = 3/6 = 1/2, so R_eq = 2 Ω." },
  { id: "p40", subject: "physics", topic: "Electricity", question: "A capacitor stores 4 × 10⁻⁴ C at 20 V. What is its capacitance?", choices: ["2 × 10⁻⁵ F", "8 × 10⁻³ F", "5 × 10⁴ F", "2 × 10⁻² F"], answer: 0, explanation: "C = Q/V = (4 × 10⁻⁴)/20 = 2 × 10⁻⁵ F = 20 μF." },
  { id: "p41", subject: "physics", topic: "Electricity", question: "In an RC circuit, one time constant τ means the capacitor charges to approximately what percentage?", choices: ["37%", "50%", "63%", "100%"], answer: 2, explanation: "τ = RC. After one time constant, the capacitor charges to approximately 63.2% of maximum voltage (1 − 1/e)." },
  { id: "p42", subject: "physics", topic: "Electricity", question: "Kirchhoff's junction rule is based on conservation of:", choices: ["Energy", "Charge", "Voltage", "Resistance"], answer: 1, explanation: "Kirchhoff's junction rule: total current entering a junction equals total current leaving, based on conservation of electric charge." },
  { id: "p43", subject: "physics", topic: "Electricity", question: "The electric field between parallel plates with 100 V separated by 0.02 m is:", choices: ["2 N/C", "50 N/C", "2000 N/C", "5000 N/C"], answer: 3, explanation: "E = V/d = 100/0.02 = 5000 V/m = 5000 N/C." },
  { id: "p44", subject: "physics", topic: "Electricity", question: "Two charges are separated by distance r. If the separation is doubled, the Coulomb force becomes:", choices: ["Half as strong", "One-quarter as strong", "Twice as strong", "Four times as strong"], answer: 1, explanation: "By Coulomb's law, F ∝ 1/r². Doubling r: F_new = F/4." },

  // Optics (continued)
  { id: "p45", subject: "physics", topic: "Optics", question: "A concave mirror with focal length 15 cm has an object at 30 cm. Where is the image?", choices: ["10 cm", "15 cm", "30 cm", "60 cm"], answer: 2, explanation: "1/f = 1/d_o + 1/d_i: 1/15 = 1/30 + 1/d_i → 1/d_i = 1/15 − 1/30 = 1/30 → d_i = 30 cm." },
  { id: "p46", subject: "physics", topic: "Optics", question: "An object at twice the focal length of a converging lens produces an image that is:", choices: ["Virtual, upright, enlarged", "Real, inverted, same size", "Real, inverted, smaller", "Virtual, upright, same size"], answer: 1, explanation: "When d_o = 2f: d_i = 2f. Magnification = −d_i/d_o = −1, so the image is real, inverted, and same size." },
  { id: "p47", subject: "physics", topic: "Optics", question: "Total internal reflection can only occur when light travels from:", choices: ["Lower to higher index of refraction", "Higher to lower index of refraction", "Either direction", "Higher to lower density gas"], answer: 1, explanation: "Total internal reflection occurs only from a denser medium (higher n) to a less dense medium (lower n) at angles greater than the critical angle." },
  { id: "p48", subject: "physics", topic: "Optics", question: "A diffraction grating with 500 lines/mm is illuminated with 600 nm light. At what angle is the first-order maximum?", choices: ["8.6°", "17.5°", "30.0°", "45.0°"], answer: 1, explanation: "d = 1/500 mm = 2 × 10⁻⁶ m. d sin θ = mλ: sin θ = (600 × 10⁻⁹)/(2 × 10⁻⁶) = 0.3 → θ ≈ 17.5°." },

  // Thermodynamics (continued)
  { id: "p49", subject: "physics", topic: "Thermodynamics", question: "The First Law of Thermodynamics is a statement of:", choices: ["Conservation of momentum", "Conservation of energy", "Conservation of charge", "Entropy always increases"], answer: 1, explanation: "The First Law (ΔU = Q − W) states that change in internal energy equals heat added minus work done — conservation of energy." },
  { id: "p50", subject: "physics", topic: "Thermodynamics", question: "A heat engine absorbs 500 J and exhausts 350 J. What is its efficiency?", choices: ["15%", "30%", "70%", "150%"], answer: 1, explanation: "Efficiency = W/Q_H = (500 − 350)/500 = 150/500 = 30%." },
  { id: "p51", subject: "physics", topic: "Thermodynamics", question: "The total entropy of an isolated system:", choices: ["Always decreases", "Always stays the same", "Can never increase", "Always stays the same or increases"], answer: 3, explanation: "The Second Law: total entropy of an isolated system either stays the same (reversible) or increases (irreversible). It never spontaneously decreases." },
  { id: "p52", subject: "physics", topic: "Thermodynamics", question: "On a PV diagram, work done by a gas during isobaric expansion is represented by:", choices: ["The slope of the curve", "The area under the curve", "The height of the curve", "The length of the curve"], answer: 1, explanation: "On a PV diagram, work = area under the curve. For isobaric processes, W = PΔV." },

  // Momentum (continued)
  { id: "p53", subject: "physics", topic: "Momentum", question: "A 2 kg ball moving east at 3 m/s collides with a 2 kg ball moving north at 4 m/s. If they stick together, what is their speed?", choices: ["2.5 m/s", "3.5 m/s", "5.0 m/s", "7.0 m/s"], answer: 0, explanation: "p_x = 6, p_y = 8. p_total = √(36+64) = 10 kg·m/s. Speed = 10/4 = 2.5 m/s." },
  { id: "p54", subject: "physics", topic: "Momentum", question: "A 3 kg and 1 kg mass are 2 m apart. The center of mass is located:", choices: ["0.5 m from the 3 kg mass", "1.0 m from each mass", "1.5 m from the 3 kg mass", "0.5 m from the 1 kg mass"], answer: 0, explanation: "x_cm = (3×0 + 1×2)/4 = 0.5 m from the 3 kg mass." },
  { id: "p55", subject: "physics", topic: "Momentum", question: "A 0.05 kg bullet embeds in a 4.95 kg block at rest. If the bullet was traveling at 400 m/s, what is the system's speed?", choices: ["2 m/s", "4 m/s", "8 m/s", "20 m/s"], answer: 1, explanation: "Conservation of momentum: (0.05)(400) = (5.0)v_f → v_f = 20/5 = 4 m/s." },
  { id: "p56", subject: "physics", topic: "Momentum", question: "A figure skater pulls her arms inward during a spin. Her angular velocity increases because:", choices: ["Her angular momentum increases", "Her moment of inertia decreases while angular momentum is conserved", "External torque speeds her up", "Her mass decreases"], answer: 1, explanation: "Angular momentum L = Iω is conserved. Pulling arms in reduces I, so ω must increase." },

  // Magnetism
  { id: "p57", subject: "physics", topic: "Magnetism", question: "A charged particle moving parallel to a magnetic field experiences:", choices: ["Maximum force", "A force perpendicular to velocity", "Zero magnetic force", "A decelerating force"], answer: 2, explanation: "F = qvB sin θ. When velocity is parallel to B, θ = 0° and sin 0° = 0, so force is zero." },
  { id: "p58", subject: "physics", topic: "Magnetism", question: "According to Faraday's law, an emf is induced in a loop when:", choices: ["A constant magnetic field passes through it", "The magnetic flux through the loop changes", "A battery is connected to it", "The loop is stationary in a uniform field"], answer: 1, explanation: "Faraday's law: induced emf = −dΦ_B/dt. An emf is induced only when flux changes." },
  { id: "p59", subject: "physics", topic: "Magnetism", question: "Lenz's law states that an induced current flows in a direction that:", choices: ["Reinforces the change in flux", "Opposes the change in flux that caused it", "Is always clockwise", "Maximizes the flux"], answer: 1, explanation: "Lenz's law: the induced current creates a magnetic field opposing the change in flux that produced it (conservation of energy)." },
  { id: "p60", subject: "physics", topic: "Magnetism", question: "A wire carries current into the page. Using the right-hand rule, the magnetic field directly above the wire points:", choices: ["To the right", "To the left", "Into the page", "Out of the page"], answer: 0, explanation: "Right-hand rule for a current-carrying wire: thumb into the page, fingers curl clockwise. Above the wire, the field points to the right." },
  { id: "p61", subject: "physics", topic: "Magnetism", question: "A solenoid with 200 turns, length 0.5 m, carrying 3 A. What is the magnetic field inside? (μ₀ = 4π × 10⁻⁷)", choices: ["4.8 × 10⁻⁴ T", "1.5 × 10⁻³ T", "7.5 × 10⁻⁴ T", "3.0 × 10⁻³ T"], answer: 1, explanation: "B = μ₀nI, n = 200/0.5 = 400. B = (4π × 10⁻⁷)(400)(3) ≈ 1.5 × 10⁻³ T." },

  // Fluids
  { id: "p62", subject: "physics", topic: "Fluids", question: "What is the pressure at the bottom of a 3 m deep pool? (ρ = 1000 kg/m³, g = 10 m/s², ignore atmospheric)", choices: ["3000 Pa", "10000 Pa", "30000 Pa", "300000 Pa"], answer: 2, explanation: "P = ρgh = (1000)(10)(3) = 30000 Pa." },
  { id: "p63", subject: "physics", topic: "Fluids", question: "Pascal's principle states that pressure applied to an enclosed fluid is:", choices: ["Absorbed by the fluid", "Transmitted undiminished to all parts", "Reduced with distance", "Only transmitted downward"], answer: 1, explanation: "Pascal's principle: pressure applied to an enclosed fluid is transmitted undiminished throughout." },
  { id: "p64", subject: "physics", topic: "Fluids", question: "According to Bernoulli's equation, as fluid speed increases in a horizontal pipe, pressure:", choices: ["Increases", "Decreases", "Remains the same", "First increases, then decreases"], answer: 1, explanation: "Bernoulli's equation shows that for a horizontal pipe, as velocity increases, pressure must decrease." },
  { id: "p65", subject: "physics", topic: "Fluids", question: "A 0.5 m³ object with density 600 kg/m³ is submerged in water. What is the net upward force? (g = 10 m/s²)", choices: ["1000 N", "2000 N", "3000 N", "5000 N"], answer: 1, explanation: "Buoyant force = (1000)(0.5)(10) = 5000 N. Weight = (600)(0.5)(10) = 3000 N. Net = 5000 − 3000 = 2000 N." },

  // Modern Physics
  { id: "p66", subject: "physics", topic: "Modern Physics", question: "In the photoelectric effect, increasing light intensity above the threshold frequency increases:", choices: ["Kinetic energy of electrons", "Number of emitted electrons", "Work function of the metal", "Threshold frequency"], answer: 1, explanation: "Higher intensity = more photons per second = more electrons ejected. Kinetic energy depends on frequency, not intensity." },
  { id: "p67", subject: "physics", topic: "Modern Physics", question: "The de Broglie wavelength is inversely proportional to a particle's:", choices: ["Energy", "Frequency", "Momentum", "Mass only"], answer: 2, explanation: "λ = h/p. Greater momentum means shorter wavelength." },
  { id: "p68", subject: "physics", topic: "Modern Physics", question: "As an object's speed approaches c, its relativistic mass:", choices: ["Decreases toward zero", "Stays constant", "Increases without bound", "Oscillates"], answer: 2, explanation: "Relativistic mass increases by γ = 1/√(1 − v²/c²). As v → c, γ → ∞, requiring infinite energy to reach c." },
  { id: "p69", subject: "physics", topic: "Modern Physics", question: "Hydrogen's emission spectrum is produced when electrons:", choices: ["Are stripped from the atom", "Transition from lower to higher energy levels", "Transition from higher to lower energy levels", "Orbit at constant energy"], answer: 2, explanation: "Emission spectra are produced when electrons drop from higher to lower energy levels, releasing photons with specific energies." },

  // Gravity
  { id: "p70", subject: "physics", topic: "Gravity", question: "If the distance between two masses is tripled, the gravitational force becomes:", choices: ["1/3 as strong", "1/6 as strong", "1/9 as strong", "3 times as strong"], answer: 2, explanation: "F ∝ 1/r². Tripling distance: F_new = F/9." },
  { id: "p71", subject: "physics", topic: "Gravity", question: "A satellite's orbital speed depends on:", choices: ["The satellite's mass only", "The orbital radius and central body's mass", "The satellite's mass and shape", "The direction of orbit only"], answer: 1, explanation: "v = √(GM/r). Orbital speed depends on the central body's mass M and orbital radius r, not the satellite's mass." },
  { id: "p72", subject: "physics", topic: "Gravity", question: "Gravitational field strength at a planet's surface is 5 N/kg. A 10 kg object weighs:", choices: ["2 N", "15 N", "50 N", "500 N"], answer: 2, explanation: "Weight = mg = (10)(5) = 50 N." },

  // Simple Harmonic Motion
  { id: "p73", subject: "physics", topic: "Simple Harmonic Motion", question: "The period of a simple pendulum depends on:", choices: ["Mass and length", "Length and gravitational acceleration", "Mass and amplitude", "Amplitude and gravity"], answer: 1, explanation: "T = 2π√(L/g). Period depends only on length L and g, not on mass or small-amplitude oscillations." },
  { id: "p74", subject: "physics", topic: "Simple Harmonic Motion", question: "A 0.5 kg mass on a spring (k = 50 N/m) oscillates. What is the period?", choices: ["0.2π s", "0.5π s", "π s", "5π s"], answer: 0, explanation: "T = 2π√(m/k) = 2π√(0.5/50) = 2π(0.1) = 0.2π s ≈ 0.63 s." },
  { id: "p75", subject: "physics", topic: "Simple Harmonic Motion", question: "In SHM, maximum speed occurs at:", choices: ["Maximum displacement", "The equilibrium position", "One-quarter displacement", "The turning points"], answer: 1, explanation: "At equilibrium, all energy is kinetic, so speed is maximum. At maximum displacement (turning points), speed is zero." },

  // ═══════════════════════════════════════════
  //  BOOK: Radioactivity – A History of a Mysterious Science
  //  by Marjorie C. Malley (2025–2026 UIL Assigned Text)
  // ═══════════════════════════════════════════

  // The Beginnings (Ch. 1)
  { id: "bk1", subject: "book", topic: "The Beginnings", question: "In what year did Henri Becquerel discover radioactivity?", choices: ["1886", "1895", "1896", "1903"], answer: 2, explanation: "Henri Becquerel discovered radioactivity in 1896 when he found that uranium minerals could darken a photographic plate without exposure to light." },
  { id: "bk2", subject: "book", topic: "The Beginnings", question: "Becquerel's discovery of radioactivity was closely connected to the recent discovery of:", choices: ["Electrons", "X-rays", "Protons", "Radio waves"], answer: 1, explanation: "Becquerel was investigating whether fluorescent minerals could emit X-rays (discovered by Wilhelm Röntgen in 1895) when he stumbled upon radioactivity." },
  { id: "bk3", subject: "book", topic: "The Beginnings", question: "What material did Becquerel use in his initial experiments that led to the discovery of radioactivity?", choices: ["Thorium salts", "Radium crystals", "Uranium minerals", "Plutonium samples"], answer: 2, explanation: "Becquerel was experimenting with uranium-containing minerals (potassium uranyl sulfate) when he discovered that they emitted penetrating rays without external energy." },
  { id: "bk4", subject: "book", topic: "The Beginnings", question: "Wilhelm Röntgen's discovery of X-rays, which set the stage for Becquerel's work, occurred in:", choices: ["1890", "1895", "1896", "1900"], answer: 1, explanation: "Röntgen discovered X-rays in 1895, which inspired Becquerel and others to investigate whether other materials could produce similar penetrating radiation." },

  // The Curies (Ch. 2)
  { id: "bk5", subject: "book", topic: "The Curies", question: "Marie Curie's birth name before marriage was:", choices: ["Marie Becquerel", "Maria Skłodowska", "Marie Joliot", "Maria Rutherford"], answer: 1, explanation: "Marie Curie was born Maria Skłodowska in Warsaw, Poland, before moving to Paris where she met and married Pierre Curie." },
  { id: "bk6", subject: "book", topic: "The Curies", question: "The Curies are credited with coining which term?", choices: ["Isotope", "Radio-activity", "Nuclear fission", "Half-life"], answer: 1, explanation: "Pierre and Marie Curie coined the term 'radio-activity' to describe the phenomenon of spontaneous radiation emission they were studying." },
  { id: "bk7", subject: "book", topic: "The Curies", question: "Which two new elements did Marie Curie discover?", choices: ["Radium and Uranium", "Polonium and Radium", "Thorium and Radium", "Polonium and Francium"], answer: 1, explanation: "Marie Curie discovered polonium (named after her native Poland) and radium through painstaking chemical separation of pitchblende ore." },
  { id: "bk8", subject: "book", topic: "The Curies", question: "Marie Curie processed approximately how much pitchblende ore by hand to isolate radium?", choices: ["1 kg", "10 kg", "100 kg", "1000 kg"], answer: 2, explanation: "Between 1899 and 1902, Marie Curie processed about 100 kg of pitchblende ore by hand in a Paris courtyard to isolate a tiny amount of radium." },
  { id: "bk9", subject: "book", topic: "The Curies", question: "Marie Curie won Nobel Prizes in which two fields?", choices: ["Chemistry and Medicine", "Physics and Chemistry", "Physics and Medicine", "Chemistry and Biology"], answer: 1, explanation: "Marie Curie won the Nobel Prize in Physics (1903, shared with Pierre and Becquerel) and in Chemistry (1911) for her discovery and isolation of radium and polonium." },

  // Rutherford, Soddy & Transmutation (Ch. 3)
  { id: "bk10", subject: "book", topic: "Rutherford & Soddy", question: "Ernest Rutherford and Frederick Soddy made their groundbreaking work on transmutation at which university?", choices: ["Cambridge University", "McGill University", "University of Manchester", "University of Vienna"], answer: 1, explanation: "Rutherford and Soddy collaborated at McGill University in Montreal, Canada, where they discovered that radioactive elements were transmuting into other elements." },
  { id: "bk11", subject: "book", topic: "Rutherford & Soddy", question: "Rutherford identified two types of radiation from uranium, which he named:", choices: ["X-rays and gamma rays", "Alpha and beta rays", "Proton and neutron rays", "Cathode and anode rays"], answer: 1, explanation: "Rutherford discovered that uranium emitted two distinct types of radiation: alpha rays (less penetrating, positively charged) and beta rays (more penetrating, negatively charged)." },
  { id: "bk12", subject: "book", topic: "Rutherford & Soddy", question: "The revolutionary idea of transmutation meant that:", choices: ["Elements could be created from nothing", "Chemical elements were not immutable — they could change into other elements", "Radiation could be stopped by lead", "Atoms were indivisible"], answer: 1, explanation: "Rutherford and Soddy proposed that radioactive atoms were spontaneously transforming (transmuting) into atoms of different elements, overturning the belief that elements were unchangeable." },
  { id: "bk13", subject: "book", topic: "Rutherford & Soddy", question: "Soddy discovered that 'emanations' from thorium were actually:", choices: ["A form of light", "An inert gas produced by transmutation", "Tiny particles of thorium", "A chemical reaction with air"], answer: 1, explanation: "Soddy realized that the 'emanation' given off by thorium was an inert (noble) gas — a product of thorium's transmutation into a different element." },
  { id: "bk14", subject: "book", topic: "Rutherford & Soddy", question: "Frederick Soddy coined the word 'isotope' in which year?", choices: ["1896", "1903", "1911", "1920"], answer: 2, explanation: "Soddy coined the term 'isotope' in 1911 when he recognized that atoms of the same element could have different atomic weights but identical chemical properties." },

  // The Radioactive Earth (Ch. 4)
  { id: "bk15", subject: "book", topic: "Radioactive Earth", question: "Radioactivity provided scientists with a new method for:", choices: ["Making gold from lead", "Dating the age of rocks and the Earth", "Creating new elements in the lab", "Measuring the speed of light"], answer: 1, explanation: "Radioactive decay rates provided a 'clock' that allowed scientists to date geological samples and estimate the age of the Earth far more accurately than previous methods." },
  { id: "bk16", subject: "book", topic: "Radioactive Earth", question: "One major question radioactivity helped resolve was:", choices: ["Whether the Earth orbits the Sun", "The source of the Sun's energy and Earth's internal heat", "The composition of the atmosphere", "The cause of earthquakes"], answer: 1, explanation: "Radioactivity explained how the Earth could maintain its internal heat over billions of years and suggested a mechanism for the Sun's energy output, resolving debates about the age of the Earth." },

  // Radioactivity & Chemistry (Ch. 6)
  { id: "bk17", subject: "book", topic: "Radiochemistry", question: "The concept of 'displacement laws' in radioactivity described:", choices: ["How elements move in the periodic table when they emit alpha or beta particles", "The physical movement of radioactive materials", "How radiation displaces electrons from atoms", "The distance radiation travels through matter"], answer: 0, explanation: "The displacement laws described how an element's position in the periodic table shifts when it undergoes radioactive decay: alpha emission moves it 2 places left, beta emission moves it 1 place right." },
  { id: "bk18", subject: "book", topic: "Radiochemistry", question: "A major puzzle in early radiochemistry was that some chemically identical radioactive substances had:", choices: ["Different colors", "Different atomic weights", "Different melting points", "Different states of matter"], answer: 1, explanation: "Researchers found 'inseparable radioelements' — substances that were chemically identical but had different radioactive properties and atomic weights. This led to the concept of isotopes." },

  // Inside the Atom (Ch. 7)
  { id: "bk19", subject: "book", topic: "Inside the Atom", question: "Rutherford's famous gold foil experiment led to the discovery of:", choices: ["The electron", "The atomic nucleus", "Radioactivity", "Isotopes"], answer: 1, explanation: "Rutherford's alpha-particle scattering experiments revealed that atoms have a tiny, dense, positively charged nucleus, overturning the 'plum pudding' model of the atom." },
  { id: "bk20", subject: "book", topic: "Inside the Atom", question: "The third type of radiation discovered (after alpha and beta) was:", choices: ["X-rays", "Neutron radiation", "Gamma rays", "Cosmic rays"], answer: 2, explanation: "Gamma rays were identified as a third type of radioactive emission — highly penetrating electromagnetic radiation with no charge, distinct from the particulate alpha and beta rays." },

  // Sequel – War & Nuclear Physics (Ch. 8)
  { id: "bk21", subject: "book", topic: "Sequel", question: "The book's narrative of radioactivity's scientific history ends with which major discovery in 1938?", choices: ["The neutron", "Nuclear fission", "The positron", "Artificial radioactivity"], answer: 1, explanation: "The book's scientific narrative spans from Becquerel's 1896 discovery to Otto Hahn, Fritz Strassmann, and Lise Meitner's discovery of nuclear fission in 1938." },
  { id: "bk22", subject: "book", topic: "Sequel", question: "Which scientists are credited with the discovery of nuclear fission in 1938?", choices: ["Rutherford and Soddy", "The Curies", "Hahn, Strassmann, and Meitner", "Bohr and Einstein"], answer: 2, explanation: "Otto Hahn and Fritz Strassmann performed the experiments, and Lise Meitner (with her nephew Otto Frisch) provided the theoretical explanation of nuclear fission." },

  // Medicine & Safety (Ch. 10)
  { id: "bk23", subject: "book", topic: "Medicine & Safety", question: "Early researchers suffered from radiation because:", choices: ["They deliberately exposed themselves", "The dangers of radiation were not yet understood", "They lacked proper equipment", "They worked with very large quantities"], answer: 1, explanation: "In the early years, the harmful effects of radiation were unknown. Researchers handled radioactive materials without protection, leading to burns, illness, and in some cases death." },
  { id: "bk24", subject: "book", topic: "Medicine & Safety", question: "Radium was initially promoted as a treatment for:", choices: ["Broken bones", "Cancer and various ailments", "Mental illness", "Infectious diseases"], answer: 1, explanation: "Radium was used in early cancer treatments and was also marketed (often dangerously) as a cure for various ailments, appearing in radium spas, tonics, and health products." },
  { id: "bk25", subject: "book", topic: "Medicine & Safety", question: "Stefan Meyer, a physicist mentioned in the book, suffered damage to his:", choices: ["Eyes from radiation exposure", "Fingers from handling radioactive materials", "Lungs from inhaling radon", "Hearing from laboratory explosions"], answer: 1, explanation: "Stefan Meyer's fingers suffered radiation damage from his work handling radioactive materials, a common injury among early radiation researchers before safety protocols existed." },

  // New Industries (Ch. 11)
  { id: "bk26", subject: "book", topic: "New Industries", question: "One of the most notorious industrial uses of radioactivity was:", choices: ["Nuclear power plants", "Radium-based luminous paint for watch dials", "Uranium-powered engines", "Radioactive food preservation"], answer: 1, explanation: "Radium-based paint was used to make watch dials and instrument panels glow in the dark. Workers (mostly young women known as 'Radium Girls') who painted the dials suffered severe radiation poisoning." },
  { id: "bk27", subject: "book", topic: "New Industries", question: "Decorative items made with uranium that were popular before the dangers were understood included:", choices: ["Uranium jewelry", "Uranium glassware", "Uranium-coated furniture", "Uranium-dyed clothing"], answer: 1, explanation: "Uranium was used to create distinctive fluorescent glassware (known as Vaseline glass) that glowed green under UV light, a popular decorative item before radiation hazards were fully appreciated." },

  // Key Figures & Themes (Ch. 12–14)
  { id: "bk28", subject: "book", topic: "Key Figures", question: "The book highlights that an unusually high number of what group entered the field of radioactivity research?", choices: ["Military personnel", "Women scientists", "Clergy members", "Industrial workers"], answer: 1, explanation: "The book notes that women entered the field of radioactivity research in unprecedented numbers, partly due to the Curies' example and the field's relative newness." },
  { id: "bk29", subject: "book", topic: "Key Figures", question: "The three main cities where radioactivity research was centered, as described in the book, were:", choices: ["London, Berlin, New York", "Paris, Manchester, Vienna", "Boston, Geneva, Tokyo", "Rome, Stockholm, Moscow"], answer: 1, explanation: "The narrative of radioactivity research primarily unfolds across Paris (the Curies), Manchester (Rutherford's later work), and Vienna (the Austrian research community)." },
  { id: "bk30", subject: "book", topic: "Key Figures", question: "The book describes radioactivity's development as spanning from France, Germany, the UK, and:", choices: ["Russia", "Canada", "Japan", "Italy"], answer: 1, explanation: "Canada played a key role because Rutherford and Soddy did their groundbreaking transmutation work at McGill University in Montreal." },

  // Decay & Transformation (Ch. 5)
  { id: "bk31", subject: "book", topic: "Decay & Transformation", question: "According to Malley's book, what term did Rutherford introduce to describe the time it takes for half of a radioactive substance to decay?", choices: ["Decay constant", "Half-life", "Transformation period", "Activity interval"], answer: 1, explanation: "Rutherford introduced the concept and term 'half-life' to quantify the rate of radioactive decay, which became one of the most important metrics in nuclear science." },
  { id: "bk32", subject: "book", topic: "Decay & Transformation", question: "The three naturally occurring radioactive decay series each end with a stable isotope of which element?", choices: ["Barium", "Bismuth", "Lead", "Gold"], answer: 2, explanation: "The uranium series, thorium series, and actinium series each terminate in a different stable isotope of lead." },
  { id: "bk33", subject: "book", topic: "Decay & Transformation", question: "What happens to an atom's position in the periodic table when it emits an alpha particle?", choices: ["It moves up two places", "It moves down two places", "It moves down one place", "It stays in the same place"], answer: 1, explanation: "Alpha decay reduces the atomic number by 2, causing the element to shift down two places in the periodic table." },

  // Measuring Radioactivity (Ch. 9)
  { id: "bk34", subject: "book", topic: "Measuring Radioactivity", question: "Which instrument was the primary tool used by early radioactivity researchers to detect radiation?", choices: ["Cloud chamber", "Gold-leaf electroscope", "Scintillation screen", "Photographic plate"], answer: 1, explanation: "The gold-leaf electroscope was the workhorse instrument of early radioactivity research. It measured radiation by detecting the ionization of air, which caused the gold leaves to converge." },
  { id: "bk35", subject: "book", topic: "Measuring Radioactivity", question: "Who developed the cloud chamber that allowed researchers to see tracks of individual radioactive particles?", choices: ["Hans Geiger", "C.T.R. Wilson", "Ernest Rutherford", "William Crookes"], answer: 1, explanation: "C.T.R. Wilson developed the cloud chamber, which made visible the paths of individual charged particles through supersaturated water vapor." },
  { id: "bk36", subject: "book", topic: "Measuring Radioactivity", question: "The unit 'curie' was originally defined based on the activity of what amount of radium?", choices: ["One milligram", "One gram", "One kilogram", "One microgram"], answer: 1, explanation: "The curie was defined as the radioactivity of one gram of radium-226, honoring the Curies' work." },

  // Sequel/Nuclear Physics (Ch. 8, continued)
  { id: "bk37", subject: "book", topic: "Sequel", question: "What particle did James Chadwick discover in 1932?", choices: ["Positron", "Neutron", "Meson", "Neutrino"], answer: 1, explanation: "Chadwick's 1932 discovery of the neutron solved the puzzle of nuclear mass and opened the door to nuclear fission." },
  { id: "bk38", subject: "book", topic: "Sequel", question: "Irène and Frédéric Joliot-Curie discovered artificial radioactivity by bombarding aluminum with:", choices: ["Neutrons", "Beta particles", "Alpha particles", "Gamma rays"], answer: 2, explanation: "The Joliot-Curies bombarded aluminum with alpha particles in 1934 and discovered that the target continued to emit radiation after the source was removed." },
  { id: "bk39", subject: "book", topic: "Sequel", question: "Which two scientists provided the theoretical explanation for nuclear fission using the liquid drop model?", choices: ["Bohr and Wheeler", "Meitner and Frisch", "Hahn and Strassmann", "Fermi and Segrè"], answer: 1, explanation: "Lise Meitner and her nephew Otto Frisch provided the first theoretical explanation of fission and coined the term 'fission.'" },

  // The Curies (continued)
  { id: "bk40", subject: "book", topic: "The Curies", question: "The Curies conducted their most famous radium isolation work in:", choices: ["A well-funded government laboratory", "A converted shed with a leaking roof", "The basement of the Sorbonne", "A private industry-funded laboratory"], answer: 1, explanation: "Marie and Pierre Curie worked in a converted shed that had previously been used as a dissecting room, with poor ventilation and a leaking roof." },
  { id: "bk41", subject: "book", topic: "The Curies", question: "What controversy surrounded Marie Curie's first Nobel Prize in Physics (1903)?", choices: ["She was initially not included in the nomination", "The Swedish Academy questioned her methods", "Pierre Curie refused to accept", "Becquerel disputed the Curies' priority"], answer: 0, explanation: "Marie Curie was initially omitted from the Nobel nomination. Pierre Curie intervened and insisted on her inclusion, reflecting the gender biases of the era." },

  // Rutherford & Soddy (continued)
  { id: "bk42", subject: "book", topic: "Rutherford & Soddy", question: "A major reason many chemists initially resisted Rutherford and Soddy's transmutation theory was that:", choices: ["It contradicted conservation of energy", "It seemed to revive the discredited idea of alchemy", "Soddy lacked academic credentials", "The experiments could not be replicated"], answer: 1, explanation: "The idea of elements spontaneously changing into others reminded scientists of alchemy, which had long been dismissed as pseudoscience." },
  { id: "bk43", subject: "book", topic: "Rutherford & Soddy", question: "Rutherford and Soddy's key evidence for transmutation came from studying which radioactive gas from thorium?", choices: ["Radon", "Thoron (thorium emanation)", "Actinon", "Radium emanation"], answer: 1, explanation: "Their careful study of thorium emanation (later called thoron) at McGill University provided critical evidence that radioactive elements were transforming into new elements." },

  // Inside the Atom (continued)
  { id: "bk44", subject: "book", topic: "Inside the Atom", question: "Niels Bohr's 1913 atomic model incorporated which revolutionary concept?", choices: ["The exclusion principle", "Quantum theory", "Special relativity", "Wave-particle duality"], answer: 1, explanation: "Bohr applied Planck's quantum theory to the atom, proposing that electrons could only occupy certain discrete energy levels." },
  { id: "bk45", subject: "book", topic: "Inside the Atom", question: "Before Chadwick's discovery, the puzzle of nuclear mass was that:", choices: ["Nuclei were too light for the number of protons", "Nuclear mass exceeded what protons alone could account for", "Mass was being created from nothing", "Protons repelled each other too strongly to form nuclei"], answer: 1, explanation: "Scientists struggled to explain why nuclear mass exceeded what protons alone could account for. Chadwick's neutron elegantly solved this puzzle." },

  // Medicine & Safety (continued)
  { id: "bk46", subject: "book", topic: "Medicine & Safety", question: "Eben Byers, the wealthy socialite mentioned in the book, suffered from:", choices: ["Severe anemia from radiation", "His jaw and skull disintegrating from radium poisoning", "Blindness from radium exposure", "Leukemia within months"], answer: 1, explanation: "Byers drank large quantities of Radithor (a radium-containing patent medicine). His bones deteriorated catastrophically, and his 1932 death helped turn opinion against radium products." },
  { id: "bk47", subject: "book", topic: "Medicine & Safety", question: "The radium dial painters ('Radium Girls') suffered primarily because they:", choices: ["Inhaled radium dust from poor ventilation", "Licked their brushes to form a fine point, ingesting radium", "Handled radium with bare hands", "Were exposed to external gamma radiation"], answer: 1, explanation: "The young women were instructed to 'lip-point' their brushes — licking them to create a fine tip — causing them to ingest significant amounts of radium paint." },

  // Themes
  { id: "bk48", subject: "book", topic: "Key Figures", question: "A major theme in Malley's book is that early radioactivity research was characterized by:", choices: ["Intense secrecy between laboratories", "Remarkable international collaboration and open exchange", "Domination by a single country", "Strict government control"], answer: 1, explanation: "Malley emphasizes that radioactivity research was remarkably international, with scientists from France, Britain, Germany, Austria, and other countries freely exchanging ideas and materials." },
  { id: "bk49", subject: "book", topic: "Key Figures", question: "Beyond Marie Curie, Malley highlights that in early radioactivity research:", choices: ["Women were entirely excluded", "Numerous women made significant contributions, though often unrecognized", "Women served only as assistants", "The field actively recruited women"], answer: 1, explanation: "Many women beyond Marie Curie contributed — including Harriet Brooks, Lise Meitner, Irène Joliot-Curie — though institutional barriers and gender bias often limited their recognition." },
  { id: "bk50", subject: "book", topic: "Key Figures", question: "Malley's book illustrates that in the history of science, new discoveries are often:", choices: ["Immediately accepted by the scientific community", "Resisted because they challenge established theories", "Driven primarily by applied technology needs", "Made by individual geniuses working alone"], answer: 1, explanation: "A central theme is that radioactivity repeatedly challenged established ideas — the permanence of elements, the age of the Earth, atomic indivisibility — and acceptance required overcoming significant resistance." },
];

// Build topic/subject indices
const SUBJECTS = {
  biology: { name: "Biology", icon: "🧬", color: "#48bb78" },
  chemistry: { name: "Chemistry", icon: "⚗️", color: "#667eea" },
  physics: { name: "Physics", icon: "⚡", color: "#ed8936" },
  book: { name: "Book", icon: "📖", color: "#ecc94b" }
};

const TOPICS = {};
SCIENCE_QUESTIONS.forEach(q => {
  const key = q.subject + "|" + q.topic;
  if (!TOPICS[key]) {
    TOPICS[key] = { subject: q.subject, topic: q.topic, count: 0 };
  }
  TOPICS[key].count++;
});

function getTopicsForSubject(subject) {
  return Object.values(TOPICS).filter(t => t.subject === subject);
}

function getQuestionsForTopic(subject, topic) {
  return SCIENCE_QUESTIONS.filter(q => q.subject === subject && q.topic === topic);
}

function getQuestionsForSubject(subject) {
  return SCIENCE_QUESTIONS.filter(q => q.subject === subject);
}

function shuffleArray(arr) {
  const a = [...arr];
  for (let i = a.length - 1; i > 0; i--) {
    const j = Math.floor(Math.random() * (i + 1));
    [a[i], a[j]] = [a[j], a[i]];
  }
  return a;
}
